INSERT INTO `daily_report_dba` VALUES ('5', '2018-01-11', '湛江全员培训通过以及申请人员数据', '--导出cme数据\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,re.login_passwd,\r\n        re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n      case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       \r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n    sc.card_no 付费卡号,\r\n      nc.name 课程名称,\r\n sc.apply_subtype 申请子类型,\r\nsc.startdate 开始日期,\r\n sc.passdate 通过日期,\r\n sc.applydate 申请日期,\r\n     \r\n sc.apply_credit  学分,\r\n sc.ic_code,\r\n sc.course_serial\r\n     -- sc.apply_credit*3 学时,\r\n      --ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where sc.course_id in (\'201700921665\',\'201700921664\'\r\n                       )\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion  \r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名, re.login_passwd,\r\n        re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n       null 付费卡号,\r\n       nc.name 课程名称,\r\n       null 申请子类型,\r\n       sc.startdate 开始日期,\r\n       sc.passdate 通过日期,\r\n      null 申请日期,   null 学分,\r\n\'\',\r\n\'\'\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where sc.course_id in (\'201700921665\',\'201700921664\'\r\n                       )\r\n                       and passdate is not null\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')', '1', '全字段 包括 电话  证件号码 秘密 用户名  姓名等', '4', '90', '2', '91', '8', '56');
