INSERT INTO `daily_report_dba` VALUES ('50', '2018-01-15', 'cme人员数据  机构数据', 'select  re.user_real_name, case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,re.user_birthday ,\r\n         re.cme_cert_type,re.cme_cert_num,\r\n           User_getUserInfo(re.login_name, 3) 学历,\r\n                     User_getUserInfo(re.login_name, 2) 职称,\r\n                     re.user_mobile, re.user_email,\r\n                      re.user_mobile 电话,\r\n                           user_getdistrict(re.d_id, 4) 医院,\r\n       re.user_hsp_name 自填医院,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别\r\n   \r\n\r\n\r\n from user_register re  where re.user_x_id like \'35%\'\r\n \r\n \r\n select  user_getdistrict(d.district_id, 1) 省,\r\n \r\n       user_getdistrict(district_id, 2) 市,\r\n       user_getdistrict(district_id, 3) 县\r\n  from user_district d where length(d.district_id)=9\r\n  and d.district_id like \'35%\'\r\n  \r\n  \r\n  \r\n   \r\n \r\n select  user_getdistrict(d.district_id, 1) 省,\r\n \r\n       user_getdistrict(district_id, 2) 市,\r\n       user_getdistrict(district_id, 3) 县\r\n  from user_district d where length(d.district_id)=9\r\n  and d.district_id like \'35%\'\r\n\r\nselect user_getdistrict(substr(h.hospital_id,0,9), 1) 省,\r\n       user_getdistrict(substr(h.hospital_id,0,9), 2) 市,\r\n       user_getdistrict(substr(h.hospital_id,0,9), 3) 县,\r\n       h.hospital_name,dd.ol_name from user_hospital h \r\n       join user_organlevel_define dd on dd.ol_id=h.ol_id where h.hospital_id like \'35%\'', '1', '', '4', '90', '2', '91', '50', '9');
INSERT INTO `daily_report_dba` VALUES ('51', '2018-01-15', '北京绑卡数据统计', 'select count(1)  from icme_student s where s.ic_code in(\r\nselect icme_ic_num from user_register@ncme re  where re.user_x_id like \'10%\'\r\nand re.icme_ic_num is not null  )\r\nand org_id=100028967\r\n\r\nselect * from icme_org o where o.org_buiness_code=\'1100ZZ01\'', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('52', '2018-01-15', 'cme按照成级别统计国家级以及非国家级学习人次', 'select t.col3,count(distinct ul.login_name||ul.course_id)\r\n   from user_login ul\r\n   join( select distinct course_id  from  ncme_course_credit where credit_type<>\'G1\' )\r\n    cs on cs.course_id=ul.course_id \r\n   join user_register re\r\n     on re.login_name = ul.login_name\r\nleft join imp_tmp_tab t on t.col1=re.jobtitle_id\r\n  where ul.course_id is not null\r\n    and ul.login_time >= to_date(\'2017-2-1\', \'yyyy-mm-dd\')\r\n    group by t.col3;', '1', '', '4', '90', '2', '91', '8', '56');
