INSERT INTO `daily_report_dba` VALUES ('54', '2018-01-15', '课程数据调取', 'select sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where    sc.course_id IN(\'201600919325\',\'201700921409\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion  \r\nselect sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where    sc.course_id IN(\'201600919325\',\'201700921409\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\');\r\n\r\n\r\n\r\nselect sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where    sc.course_id IN(\'F01600920544\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\nand re.user_x_id like \'21%\'\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion  \r\nselect sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where    sc.course_id IN(\'F01600920544\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\nand re.user_x_id like \'21%\';\r\n\r\n\r\n\r\n\r\n\r\n\r\nselect sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where    sc.course_id IN(\'201100008182\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\nand re.user_x_id like \'19%\'\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion  \r\nselect sc.course_id, re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where    sc.course_id IN(\'201100008182\'\r\n)\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\nand re.user_x_id like \'19%\';', '1', '', '4', '90', '2', '91', '8', '56');
