INSERT INTO `daily_report_dba` VALUES ('56', '2018-01-15', '导出北京市精神卫生知识全员培训 2017-12-07-033(京) 15241 \r\n社区卫生人员继续医学教育必修课程-预防保健课程  2017-12-07-015(京)  13597 通讯录至项目申报系统', '--nproject_zhixing_book_log 插入到学员通讯录\r\n    insert into nproject_zhixing_book\r\n      (id,\r\n       project_id,\r\n       zhixing_id,\r\n       name,\r\n       zhicheng,\r\n       zhiwu,\r\n       xueli,\r\n       danwei,\r\n       phone,\r\n       addtime,\r\n       source_flag,\r\n       ic_code)\r\n      SELECT nproject_zhixing_book_seq.nextval,\r\n             15241,\r\n             11814,\r\n              re.user_real_name,\r\n             d.jobtitle_name,\r\n                        \r\n\'无\',\r\n        ed.edulevel_name,\r\n            re.user_hsp_name ,\r\n            \'\',\r\n             sysdate,\r\n             2,\r\n             os.user_name\r\n--select count(1) \r\n from ncme_study_course_over@hysh  os  \r\njoin user_register@hysh  re on re.icme_ic_num=os.user_name\r\nleft join  user_jobtitle_define@hysh  d on d.jobtitle_id=re.jobtitle_id\r\nleft join  user_edulevel_define@hysh  ed  on  ed.edulevel_id=re.edulevel_id\r\nwhere course_id=\'201700921175\';\r\n select count(1) from nproject_zhixing_book b\r\n     where b.project_id = 15241\r\n       and b.zhixing_id = 11814;\r\n\r\n    update nproject_project p\r\n       set p.student_num = 211855\r\n     where p.id = 15241;\r\n    commit;\r\n\r\n\r\n\r\n\r\n--11813	13597\r\n\r\n    insert into nproject_zhixing_book\r\n      (id,\r\n       project_id,\r\n       zhixing_id,\r\n       name,\r\n       zhicheng,\r\n       zhiwu,\r\n       xueli,\r\n       danwei,\r\n       phone,\r\n       addtime,\r\n       source_flag,\r\n       ic_code)\r\n      SELECT nproject_zhixing_book_seq.nextval,\r\n             13597,\r\n             11813,\r\n              re.user_real_name,\r\n             d.jobtitle_name,\r\n                        \r\n\'无\',\r\n        ed.edulevel_name,\r\n            re.user_hsp_name ,\r\n            \'\',\r\n             sysdate,\r\n             2,\r\n             cs.ic_code\r\n--select count(1) \r\n from  ncme_study_credit@hysh  cs\r\njoin user_register@hysh  re on re.login_name=cs.user_name\r\nleft join  user_jobtitle_define@hysh  d on d.jobtitle_id=re.jobtitle_id\r\nleft join  user_edulevel_define@hysh  ed  on  ed.edulevel_id=re.edulevel_id\r\nwhere  cs.course_serial = \'2017-12-07-015(京)\'\r\n   and  cs.course_id in (\r\n                        \'201700921193\',\r\n\'201700921189\',\r\n\'201700921119\',\r\n\'201700921120\',\r\n\'201700921188\',\r\n\'201700921191\',\r\n\'201700921192\',\r\n\'201700921187\',\r\n\'201700921186\',\r\n\'201700921195\',\r\n\'201700921198\',\r\n\'201700921190\'\r\n)\r\n;\r\n\r\n\r\n select count(1) from nproject_zhixing_book b\r\n     where b.project_id = 13597\r\n       and b.zhixing_id = 11813;\r\n\r\n    update nproject_project p\r\n       set p.student_num = 22521\r\n     where p.id = 13597;\r\n    commit;', '1', '', '4', '90', '2', '91', '44', '53');
INSERT INTO `daily_report_dba` VALUES ('57', '2018-01-17', 'icme 学分数据', '--非项目学分明细\r\n\r\n       \r\n       \r\n\r\nselect s.ic_code \"人员卡号\",\r\n       s.name    \"姓名\",\r\n       s.card_number 证件号码,\r\n\r\n       \r\n          nops.score_activity_date \" 学分日期\",\r\n        \r\n           st.score_type_name \"学分类别\",\r\n             st.score_type_name \"学分子类别\",\r\n            psu.subject_name \"学科\",\r\n       pa.activity_name \"形式\",\r\n        nops.class_score \"学分\",\r\n       nops.class_hour  \"学时\",\r\n     decode(nops.score_check_flag,1,\'仅保存\',0,\'未通过\',3,\'待审核\',4,\'通过\') \"学分状态\",\r\ndecode (nops.is_valid,1,\'有效\',\'无效\'),\r\n\r\n       go.org_name \"授分单位\",\r\n  \r\n       decode (nops.source_flag ,2,\'刷卡上传\',3,\'网上学习\',1,\'icme\',\'手工补录\') \"学分来源\"\r\n      \r\n      \r\n--项目学分明细\r\nselect s.ic_code \"人员卡号\",\r\n      -- s.name    \"姓名\",\r\n       s.card_number 证件号码,\r\n       \r\n      to_char(ps.score_activity_date,\'yyyy\') \" 学分年度\",\r\n      su.subject_id 课题id,\r\n       su.subject_name \"课题名称\",\r\n       st.score_type_name \"学分类别\",\r\n       psu.subject_name \"a学科\",\r\n       pa.activity_name \"形式\",\r\n       go.org_name \"授分单位\",\r\n        decode(ps.score_check_flag,1,\'仅保存\',0,\'未通过\',3,\'待审核\',4,\'通过\') \"学分状态\",\r\n       decode (ps.source_flag ,2,\'刷卡上传\',3,\'网上学习\',1,\'icme\',\'手工补录\') \"学分来源\",\r\n     su.subject_class_score\"学分\",\r\n       su.subject_class_hour\"学时\"\r\n\r\n--select  count(*)\r\n  from icme.icme_project pro,\r\n  icme.icme_subject       su,\r\n  icme.icme_project_score ps,\r\n       icme.icme_student         s,\r\n       icme.icme_org             so,\r\n       icme.icme_org             org,\r\n       icme.icme_org             go,\r\n       icme.icme_org             to_org,\r\n       icme.icme_score_type      st,\r\n       icme.icme_score_type pst,\r\n       icme.icme_project_subject psu,icme.icme_project_activity pa\r\n where ps.ic_code = s.ic_code\r\n and ps.subject_id=su.subject_id\r\n and su.project_id=pro.project_id\r\n   and s.org_id = so.org_id\r\n   and so.org_code like org.org_code || \'%\'\r\n   and org.org_buiness_code = \'310000\'\r\n   and ps.give_org_id = go.org_id\r\n   and ps.to_org_id = to_org.org_id\r\n   and ps.score_activity_date between to_date(\'2010-1-1\', \'yyyy-MM-dd\') and\r\n       sysdate\r\n   and pro.score_type_id=st.score_type_id\r\n   and st.score_type_parent_id=pst.score_type_id\r\n   and pro.subject_id=psu.subject_id\r\nand pro.activity_id=pa.activity_id;\r\n--select  count(*)\r\n  from icme.icme_noproject_score nops,\r\n       icme.icme_student         s,\r\n       icme.icme_org             so,\r\n       icme.icme_org             org,\r\n       icme.icme_org             go,\r\n       icme.icme_org             to_org,\r\n       icme.icme_score_type      st,\r\n       icme.icme_score_type pst,\r\n       icme.icme_project_subject psu,icme.icme_project_activity pa\r\n\r\n\r\n where nops.ic_code = s.ic_code\r\n\r\n   and s.org_id = so.org_id\r\n   and so.org_code like org.org_code || \'%\'\r\n   and org.org_buiness_code = \'310000\'\r\n   and nops.give_score_org_id = go.org_id\r\n   and nops.to_org_id = to_org.org_id\r\n   and nops.score_activity_date between to_date(\'2010-1-1\', \'yyyy-MM-dd\') and\r\n     sysdate\r\n   and nops.score_type_id=st.score_type_id\r\n   and st.score_type_parent_id=pst.score_type_id\r\n   and nops.specialty_id=psu.subject_id\r\nand nops.activity_type_id=pa.activity_id\r\nand nops.source_flag<>3\r\n--select * from icme_org os where os.org_buiness_code=\'310000\'', '1', '', '6', '115', '2', '91', '50', '9');
