INSERT INTO `daily_report_dba` VALUES ('65', '2018-01-16', '陕西省副高以上职称人员数据', 'select user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       user_getdistrict(re.d_id, 4) 医院, re.user_hsp_name 自填医院,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n         when \'f\' then\r\n          \'女\'\r\n         when \'m\' then\r\n          \'男\'\r\n       end as 性别,\r\n       to_char(sysdate, \'yyyy\') - to_char(re.user_birthday, \'yyyy\') 年龄,\r\n       re.cme_cert_num 证件号码,\r\n       User_getUserInfo(re.login_name, 2) 职称,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n       re.user_mobile 电话\r\n\r\n  from user_register re\r\n where re.user_x_id like \'41%\'\r\n      \r\n   and re.jobtitle_id in\r\n       (select j.jobtitle_id\r\n          from user_jobtitle_define j\r\n         where j.jobtitle_name like \'%主任%\'\r\n            or j.jobtitle_name like \'%教授%\')', '1', '省、市、县、医院、自填医院、姓名、性别、年龄、证件号码、职称、科室、电话号码', '4', '90', '2', '91', '8', '56');
