INSERT INTO `daily_report_dba` VALUES ('67', '2018-01-16', 'cme人员数据  机构数据 北京', 'select  user_getdistrict(d.district_id, 1) 省,\r\n \r\n       user_getdistrict(district_id, 2) 市,\r\n       user_getdistrict(district_id, 3) 县\r\n  from user_district d where length(d.district_id)=9\r\n  and d.district_id like \'10%\'\r\n\r\nselect user_getdistrict(substr(h.hospital_id, 0, 9), 1) 省,\r\n       user_getdistrict(substr(h.hospital_id, 0, 9), 2) 市,\r\n       user_getdistrict(substr(h.hospital_id, 0, 9), 3) 县,\r\n       substr(h.hospital_id, 0, 9) 区县id,\r\n       h.hospital_name 医院, h.hospital_id 医院id,\r\n       dd.ol_name\r\n  from user_hospital h\r\n  join user_organlevel_define dd\r\n    on dd.ol_id = h.ol_id\r\n where h.hospital_id like \'10%\'', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('68', '2018-01-16', 'icme 机构数据 北京', 'select o4.org_id,o4.org_name,\r\no3.org_id,o3.org_name,\r\no2.org_id,o2.org_name,\r\no1.org_id,o1.org_code,o1.org_name\r\n from  icme_org o1\r\n\r\njoin icme_org o2 on o1.parent_org_id=o2.org_id\r\n\r\njoin icme_org o3 on o2.parent_org_id=o3.org_id\r\n\r\njoin icme_org o4 on o3.parent_org_id=o4.org_id\r\n where \r\n o4.org_code=\'00020023\'', '1', '', '6', '115', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('69', '2018-01-16', '课程数据调取', 'select sc.course_id,\r\n       re.user_real_name,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n       re.user_hsp_name 单位,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n       User_getUserInfo(re.login_name, 2) 职称\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n  join ncme_credit_type ct\r\n    on ct.credit_type = sc.credit_type\r\n where sc.course_id IN\r\n       (select col7 from imp_tmp_tab where col7 is not null)\r\n   and sc.user_x_id like \'19%\'\r\n   and sc.startdate >= to_date(\'2017-2-1\', \'yyyy-mm-dd\')\r\n   and sc.startdate < to_date(\'2018-2-1\', \'yyyy-mm-dd\')', '1', '', '4', '90', '2', '91', '8', '56');
