INSERT INTO `daily_report_dba` VALUES ('74', '2018-01-17', '上海项目申报脚本', 'alter table nproject_project add ZHIXING_REPORT INTEGER default 0;\r\nalter table nproject_project add STUDENT_COUNT NUMBER default 0;\r\n\r\nmerge into nproject_project x \r\nusing (select project_id, count(*) c from nproject_zhixing_book group by project_id\r\n) y on (x.id=y.project_id)\r\nwhen matched then \r\n  update set x.student_count=y.c;\r\n  \r\n  \r\n  \r\n  \r\nupdate nproject_project np set np.student_count = 0 where np.zhixing_status in (0,1);\r\n\r\n\r\n\r\n\r\nupdate nproject_project set zhixing_report=50  where  id in(  \r\n   SELECT t1.id FROM \r\n   ( SELECT p.id, count(z.project_id) pz\r\n      FROM nproject_project p\r\n      left join nproject_zhixing z on z.project_id = p.id\r\n      where z.report in (1,2) and p.gongbu_code is not null  \r\n       group by p.id,z.project_id\r\n    )t1,   \r\n    (\r\n    SELECT p.id, count(st.project_id) ps\r\n      FROM nproject_project p\r\n      left join nproject_stage st on st.project_id = p.id\r\n      where  p.gongbu_code is not null  \r\n      group by p.id,st.project_id\r\n    ) t2 where t1.id  = t2.id  and pz<ps );\r\n    \r\n    \r\n    \r\n    update nproject_project set zhixing_report=100  where  id in(  \r\n   SELECT t1.id FROM \r\n   ( SELECT p.id, count(z.project_id) pz\r\n      FROM nproject_project p\r\n      left join nproject_zhixing z on z.project_id = p.id\r\n      where z.report in (1,2) and p.gongbu_code is not null   \r\n       group by p.id,z.project_id\r\n    )t1,   \r\n    (\r\n    SELECT p.id, count(st.project_id) ps\r\n      FROM nproject_project p\r\n      left join nproject_stage st on st.project_id = p.id\r\n      where  p.gongbu_code is not null  \r\n      group by p.id,st.project_id\r\n    ) t2 where t1.id  = t2.id  and pz>=ps );', '1', '', '66', '143', '7', '114', '8', '9');
