INSERT INTO `daily_report_dba` VALUES ('84', '2018-01-18', '上海17年数据调取', 'select sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n    \r\n       re.user_real_name 姓名,\r\n       re.user_hsp_name 单位,\r\n      nc.name 课程名称,\r\n\r\nsc.apply_credit  学分,\r\n     -- sc.apply_credit*3 学时,\r\n  ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where     sc.user_x_id like \'19%\'\r\n    and sc.applydate<to_date(\'2018-1-1\',\'yyyy-MM-dd\')\r\n    union all\r\n    \r\n    \r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n    \r\n       re.user_real_name 姓名,\r\n       re.user_hsp_name 单位,\r\n      nc.name 课程名称,\r\n\r\nsc.apply_credit  学分,\r\n     -- sc.apply_credit*3 学时,\r\n  ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit_history sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where     sc.user_x_id like \'19%\'\r\n    and sc.applydate>=to_date(\'2017-1-1\',\'yyyy-MM-dd\')', '1', '', '4', '90', '2', '91', '8', '58');
