INSERT INTO `daily_report_dba` VALUES ('104', '2018-01-22', '根据江苏省机构对应关系修改人员机构', 'select * from   imp_tmp_tab for update \r\n\r\n\r\nselect * from user_hospital h where \r\n\r\n\r\nmerge into imp_tmp_tab t \r\nusing  (select * from jc_org_info o where o.status=1) h on (t.col1=h.org_name)\r\nwhen matched then update set t.col3=h.org_id\r\n  where col3 is null ;\r\n\r\nupdate imp_tmp_tab set col1=trim(col1) where col3 is null ;\r\n\r\n\r\nselect * from   imp_tmp_tab where col3 is null ;\r\n\r\nselect * from jc_org_info o where o.status=1\r\n\r\n\r\nselect user_getdistrict(substr(h.hospital_id, 0, 9), 1) 省,\r\n       user_getdistrict(substr(h.hospital_id, 0, 9), 2) 市,\r\n       user_getdistrict(substr(h.hospital_id, 0, 9), 3) 县,\r\n       substr(h.hospital_id, 0, 9) 区县id,\r\n       h.hospital_name 医院, h.hospital_id 医院id,\r\n       dd.ol_name\r\n  from user_hospital h\r\n  join user_organlevel_define dd\r\n    on dd.ol_id = h.ol_id\r\n where h.hospital_id like \'20%\'\r\n \r\n \r\n \r\n select  distinct re.user_hsp_name from user_register re where re.user_x_id like \'20%\'', '1', '', '4', '90', '2', '97', '8', '58');
