INSERT INTO `daily_report_dba` VALUES ('114', '2018-01-24', '调取数据', 'select \r\n         user_getdistrict(re.user_x_id, 1) 省,\r\n         user_getdistrict(re.user_x_id, 2) 市,\r\n         user_getdistrict(re.user_x_id, 3) 县,\r\n            user_getdistrict(re.d_id, 4) 单位名称,\r\n            re.login_name,cs.apply_subtype 学习卡类型\r\n            ,cs.startdate 开始学习时间, case NCME_getStudyStatus(cs.user_name,\r\n                            cs.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态\r\n \r\n          from ncme_study_credit cs join \r\n user_register re  on re.login_name=cs.user_name\r\n \r\n  where \r\n  cs.startdate<to_date(\'2017-7-1\',\'yyyy-mm-dd\')\r\n  and re.d_id in(\r\n\'200110040004\',\r\n\'1000400000871\',\r\n\'1000300000006\',\r\n\'1200210850066\',\r\n\'1901300000092\',\r\n\'1100100000121\'\r\n)\r\n\r\n\r\nunion all\r\nselect\r\n         user_getdistrict(re.user_x_id, 1) 省,\r\n         user_getdistrict(re.user_x_id, 2) 市,\r\n         user_getdistrict(re.user_x_id, 3) 县,\r\n            user_getdistrict(re.d_id, 4) 单位名称,\r\n            re.login_name,\'\' 学习卡类型\r\n            ,sc.startdate 开始学习时间, case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态\r\n \r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  \r\n  where \r\n  sc.startdate<to_date(\'2017-7-1\',\'yyyy-mm-dd\')\r\n  and  sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\n and  re.d_id in(\r\n\'200110040004\',\r\n\'1000400000871\',\r\n\'1000300000006\',\r\n\'1200210850066\',\r\n\'1901300000092\',\r\n\'1100100000121\'\r\n)', '1', '', '4', '90', '2', '91', '8', '58');
