INSERT INTO `daily_report_dba` VALUES ('116', '2018-01-24', '执行脚本', 'update zyy_user_extend t set t.PHYSICIANS_QUALIFICATION_LEVEL=2,\r\nt.PHYSICIANS_QUALIFICATION_TYPE = 1 where t.zyy_user_id in (\r\nselect DISTINCT u.id\r\n  from zyy_user u\r\n  join zyy_user_extend ue\r\n    on u.id = ue.zyy_user_id\r\n   and u.zyy_user_status > 0\r\n  left join zyy_residency_user_extend rue\r\n    on u.id = rue.zyy_user_id\r\n  join zyy_base_residency br\r\n    on u.id = br.RESIDENCY_ID\r\n  join zyy_base b\r\n    on br.base_id = b.id\r\n  join zyy_base_std bs\r\n    on b.base_std_id = bs.id \r\n\r\n   and bs.status > 0\r\n  join zyy_org o\r\n    on u.zyy_user_org_id = o.id\r\n   and o.status > 0\r\n  join zyy_recruit_year y\r\n    on br.RECRUIT_YEAR_ID = y.id\r\n  join zyy_hospital_base_code hbc\r\n    on u.zyy_user_org_id = hbc.org_id\r\n   and hbc.base_id = br.base_id\r\n   and hbc.base_code is not null\r\n   and hbc.org_code is not null\r\n  join em_examinee ee\r\n    on \'rct\' || u.id = ee.em_user_id\r\n   and ee.status = 2\r\n  join em_exam_type et\r\n    on et.exam_id = ee.exam_id\r\n  left join ZYY_SYNC_USER_INFO sui\r\n    on u.id = sui.ZYY_USER_ID\r\n   and sui.code = \'800001\'\r\n where ((sui.id \r\n\r\n is not null and\r\n       ((sui.status = 1 and sui.UPDATE_DATE < ue.LAST_UPDATE_DATE) or\r\n       (sui.status = 0 and (sysdate - 1 / 24) > sui.update_date))) or\r\n       (sui.id \r\n\r\n is null))\r\n   and u.zyy_user_type = 21\r\n   and u.ZYY_USER_PROVINCE_ID = 1000000\r\n   and u.zyy_user_status != -1\r\n)', '1', 'expor_user_table_tmp  .3 拿到线上', '76', '108', '2', '89', '51', '9');
