INSERT INTO `daily_report_dba` VALUES ('137', '2018-01-26', '添加16届第四学期  课程 和  人员\r\n添加17届第二学期  课程 和  人员', '--云南（xiangyitopboxzz_anhui）\r\n--添加对应oracle的学科id\r\ndeclare @c int\r\ndeclare @seq int\r\n set @seq=8204026\r\ndeclare cur cursor for SELECT id FROM  DEPARTMENT where  semester=\'160004\' and oracleid is null order by 1\r\nopen cur\r\n   fetch next from cur into @c\r\n   WHILE @@fetch_status=0\r\n    begin\r\n      set @seq=@seq+1\r\n      print @seq\r\n      update DEPARTMENT set oracleid=@seq where id=@c and semester=\'160004\'\r\n      fetch next from cur into @c\r\n    end\r\nclose cur\r\ndeallocate cur\r\n\r\n2014届分类\r\n820401 医学\r\n820402 护理\r\n\r\n2016届分类\r\n201601 医学\r\n201602 护理\r\n\r\n2017届分类\r\n201701	2017届农村医学\r\n201702	2017届农村护理\r\n\r\n\r\n--添加对应oracle的单元id\r\ndeclare @c int\r\ndeclare @seq int\r\n set @seq=82040419\r\ndeclare cur cursor for select unitid from unit where newunitid is null and departmentid \r\nin (select departmentid from DEPARTMENT where semester=\'160004\') order by 1\r\nopen cur\r\n   fetch next from cur into @c\r\n   WHILE @@fetch_status=0\r\n    begin\r\n      set @seq=@seq+1\r\n      print @seq\r\n      update unit set newunitid=@seq where unitid=@c\r\n      fetch next from cur into @c\r\n    end\r\nclose cur\r\ndeallocate cur\r\n\r\n--在sqlserver查询单元数据\r\n select a.newunitid,b.oracleid,\'820403\',a.unitname,\'0\' from unit a,department b where \r\n a.departmentid=b.departmentid  and   b.semester=\'160004\'\r\n--插入 oracle\r\nselect * from TOPBOX_UNIT where  semester_id=\'820403\'  for update\r\n\r\n\r\n \r\n\r\n--添加对应oracle的课件id\r\n \r\ndeclare @c int\r\ndeclare @seq int\r\n set @seq=82041371\r\ndeclare cur cursor for SELECT coursewareid    FROM courseware    where oracleid is null  and \r\nunitid in(\r\n select unitid from unit where departmentid \r\nin (select departmentid from DEPARTMENT where semester=\'160004\')) order by 1\r\nopen cur\r\n   fetch next from cur into @c\r\n   WHILE @@fetch_status=0\r\n    begin\r\n      set @seq=@seq+1\r\n      print @seq\r\n      update courseware set oracleid=@seq where coursewareid=@c\r\n      fetch next from cur into @c\r\n    end\r\nclose cur\r\ndeallocate cur\r\n\r\n--学科\r\nselect oracleid,\'201602\',\'201604\',departmentname,1 from DEPARTMENT where semester=\'160004\'\r\n--to oracle\r\nselect *  from Topbox_Department where semester_id=\'201604\'     for update ;\r\n\r\n\r\n--单元\r\n select a.newunitid,b.oracleid,\'201604\',a.unitname,\'0\' from unit a,department b where \r\n a.departmentid=b.departmentid  and   b.semester=\'160004\'\r\n--to oracle\r\nselect * from TOPBOX_UNIT where  semester_id=\'160004\'  for update;\r\n\r\n\r\n--课件\r\nSELECT a.oracleid,b.newunitid ,a.coursewarename\r\n FROM courseware a , ( select * from unit where departmentid \r\nin (select departmentid from DEPARTMENT where semester=\'160004\')) b  where a.unitid=b.unitid \r\nand oracleid >=82047868;\r\n--to oracle\r\nselect id,unit_id,name from TOPBOX_COURSEWARE where /* id like \'8204%\'*/1=2 for update  \r\n\r\n使用本地包yunnan_shuju 导入oracle的TOPBOX_COURSESTUDY_yunnan表里\r\n\r\n\r\nselect * from courseware where coursewarename=\'冷疗法 热疗法\'\r\nselect * from courseware where oracleid is   null \r\nselect * from courseware where oracleid is not null order by oracleid desc \r\nSELECT * FROM  DEPARTMENT  \r\nwhere \r\nsemester=100001 and oracleid between 8204001 and 8204014\r\n\r\nSELECT *    FROM courseware    where  \r\nunitid in(\r\n select unitid from unit where departmentid \r\nin(select departmentid from department where \r\nsemester=100001 and oracleid between 8204015 and 8204025))\r\n\r\n\r\nselect distinct *  from courseware where unitid in(\r\n select unitid from unit where departmentid \r\nin(select departmentid from department where     oracleid between 8204015 and 8204025)) order by 2,3\r\n \r\n\r\nselect distinct *  from courseware where unitid in(\r\n select unitid from unit where departmentid \r\nin(select departmentid from department where     oracleid between 8204015 and 8204025)) order by 2,3\r\n\r\n select a.oracleid,b.newunitid,a.coursewarename from courseware a,unit b where a. unitid=b.unitid and b.departmentid in \r\n(select departmentid from department where     oracleid between 8204015 and 8204025)\r\n \r\nselect a.newunitid,b.oracleid,a.unitname from unit a,department b where a.departmentid=b.departmentid and b.departmentid \r\nin(select departmentid from department where     oracleid between 8204015 and 8204025)\r\n\r\n\r\n\r\n---oracle topbox\r\n\r\nselect * from TOPBOX_CLASS where id=8204\r\nselect * from TOPBOX_SEMESTER where class_id =8204--学期\r\nselect * from topbox_speciality where class_id=2016 for update  --批次\r\n\r\nselect *\r\n  from Topbox_Department\r\n where id like \'8204%\' \r\n   for update --Departmentid\r\nselect * from TOPBOX_UNIT where  id like \'8204%\' \r\nselect * from TOPBOX_COURSEWARE where /* id like \'8204%\'*/1=2 for update   \r\n\r\n--新批次加人\r\n   insert into TOPBOX_SCORE_CACHE\r\n  (id, icnum, org_id, class_id, semester_id, speciality_id, department_id)      \r\n select Topbox_Score_Cache_Seq.Nextval,a.icnum, a.org_id, a.class_id, a.semester_id, b.speciality_id,b.id\r\n   from topbox_userstudyinfo a,\r\n   (select speciality_id,id from Topbox_Department where semester_id = 201601) b\r\n  where class_id = 2016\r\n\r\n--老批次加人\r\n\r\ninsert into TOPBOX_SCORE_CACHE\r\n  (id, icnum, org_id, class_id, semester_id, speciality_id, department_id)\r\n\r\n  (select Topbox_Score_Cache_Seq.Nextval,\r\n          a.icnum,\r\n          a.org_id,\r\n          \'2017\',\r\n          b.semester_id,\r\n          b.speciality_id,\r\n          b.id\r\n     from (select a.icnum, a.org_id\r\n             from TOPBOX_SCORE_CACHE a\r\n            where class_id = 2017\r\n            group by a.icnum, a.org_id) a,\r\n          (select * from Topbox_Department where semester_id = 201701) b)\r\n\r\n\r\n\r\n--select * from TOPBOX_CLASS where /*name=\'郭式基金\' for update */org_id=230 for update \r\n--select * from TOPBOX_SEMESTER where class_id =8205 \r\n--添加地区\r\nselect * from topbox_organization t where name =\'湘西自治州\' for update \r\n--修改学员信息\r\nselect * from topbox_userstudyinfo t where icnum=69000459\r\n--修改学员状态\r\n  select state\r\n  from topbox_userinfo\r\n where icnum in (\'01220679\', \'01246147\')\r\n   for update\r\n\r\n\r\n--添加学员(3张表)\r\nselect * from topbox_userstudyinfo where icnum in (\r\n 52002765 ,\r\n 52006103  ,\r\n 22110513) for update ;\r\nselect * from topbox_userinfo where icnum in (\r\n 52006103) for update ;\r\n   注：state为0表示删除（页面不可见），1表示正常 select state from topbox_userinfo where icnum in (01218683)\r\nselect * from \r\n TOPBOX_USERINFODETAIL where icnum in (22110513  ,\r\n 52006103) for update ;', '1', '', '78', '203', '7', '214', '8', '62');
