INSERT INTO `daily_report_dba` VALUES ('149', '2018-01-29', '北京修改人员地区', 'select * from imp_tmp_tab for update \r\n\r\n\r\n\r\n\r\nselect count(1)  from icme_student@icmeyun\r\n\r\n truncate table icme_all_student;\r\n insert into icme_all_student\r\n select * from  icme_student@icmeyun\r\n where substr(ic_code,0,2) in(\'11\',\'42\');\r\n create table icme_all_org  as \r\n select * from icme_org@icmeyun\r\n where org_code like \'00020023%\';\r\n \r\n select st.ic_code,\r\n        re.user_id,\r\n        re.login_name,\r\n        t.col2,\r\n        a.id,\r\n        a.parenr_id,\r\n        a.city_name, a.cme_org_id,--e.work_counties,\r\n        t.col3,col10,col11,re.user_x_id\r\n   from icme_all_student st\r\n   join icme_all_org o\r\n     on st.org_id = o.org_id\r\n   join imp_tmp_tab t\r\n     on o.org_code like t.col6 || \'%\'\r\n   join user_register re\r\n     on re.icme_ic_num = st.ic_code\r\n   join jc_area a\r\n     on a.cme_org_id = t.col2\r\n     --join jc_user_extend e on e.user_id=re.user_id\r\n  where re.user_x_id <> t.col2\r\n    and t.col4 is not null;\r\n \r\n \r\n merge into jc_user_extend e using(\r\n select col1,col2,col3 from imp_tmp_tab where col4 is  null) aa on (aa.col1=e.user_id)\r\n when matched then update set e.work_province=\'11\',\r\n   e.work_city=col3,\r\n   e.work_counties=aa.col2\r\n   \r\n   select * from jc_user_extend where  user_id in (\r\n select col1 from imp_tmp_tab where col4 is  null)\r\n   \r\n   \r\n \r\n \r\n \r\n \r\n \r\n \r\n insert into imp_tmp_tab(col1,col2,col3)\r\n  select re.user_id,a.id,a.parenr_id  from icme_all_student st\r\n join icme_all_org o  on st.org_id=o.org_id\r\n join imp_tmp_tab t \r\n on o.org_code like   t.col6||\'%\'\r\n join user_register re on re.icme_ic_num=st.ic_code\r\n join jc_area a on a.cme_org_id=t.col2\r\n where  re.user_x_id<>t.col2;\r\n \r\n  \r\n insert into imp_tmp_tab(col1,col2,col3)\r\n  select re.user_id,a.id,a.parenr_id  from icme_all_student st\r\n join icme_all_org o  on st.org_id=o.org_id\r\n join imp_tmp_tab t \r\n on o.org_code like   t.col6||\'%\'\r\n join user_register re on re.icme_ic_num=st.ic_code\r\n join jc_area a on a.cme_org_id=t.col2\r\n where  re.user_x_id<>t.col2;\r\n \r\n select * from imp_tmp_tab where col4 is  null\r\n \r\n \r\n \r\n select *  from imp_tmp_tab where col4 is not  null\r\n and col6 is null \r\n \r\n \r\n \r\n \r\n \r\n \r\n merge into jc_user_extend e using(\r\n select col3,col4,col5 from imp_tmp_tab where col4 is not  null\r\n and col6 is null ) aa on (aa.col3=e.user_id)\r\n when matched then update set e.work_province=\'11\',\r\n   e.work_city=col4,\r\n   e.work_counties=aa.col5\r\n   \r\n   select * from jc_user_extend where  user_id in (\r\n select col1 from imp_tmp_tab where col4 is  null)', '1', '', '208', '209', '2', '89', '8', '58');
INSERT INTO `daily_report_dba` VALUES ('150', '2018-02-02', '北京修改人员地区', 'select * from imp_tmp_tab for update \r\n\r\n\r\n\r\n\r\nselect count(1)  from icme_student@icmeyun\r\n\r\n truncate table icme_all_student;\r\n insert into icme_all_student\r\n select * from  icme_student@icmeyun\r\n where substr(ic_code,0,2) in(\'11\',\'42\');\r\n create table icme_all_org  as \r\n select * from icme_org@icmeyun\r\n where org_code like \'00020023%\';\r\n \r\n select st.ic_code,\r\n        re.user_id,\r\n        re.login_name,\r\n        t.col2,\r\n        a.id,\r\n        a.parenr_id,\r\n        a.city_name, a.cme_org_id,--e.work_counties,\r\n        t.col3,col10,col11,re.user_x_id\r\n   from icme_all_student st\r\n   join icme_all_org o\r\n     on st.org_id = o.org_id\r\n   join imp_tmp_tab t\r\n     on o.org_code like t.col6 || \'%\'\r\n   join user_register re\r\n     on re.icme_ic_num = st.ic_code\r\n   join jc_area a\r\n     on a.cme_org_id = t.col2\r\n     --join jc_user_extend e on e.user_id=re.user_id\r\n  where re.user_x_id <> t.col2\r\n    and t.col4 is not null;\r\n \r\n \r\n   select * from jc_user_extend where  user_id in (\r\n select col1 from imp_tmp_tab where col4 is  null);\r\n merge into jc_user_extend e using(\r\n select col1,col2,col3 from imp_tmp_tab where col4 is  null) aa on (aa.col1=e.user_id)\r\n when matched then update set e.work_province=\'11\',\r\n   e.work_city=col3,\r\n   e.work_counties=aa.col2;\r\n   \r\n   \r\n    select * from user_register where  user_id in (\r\n\r\n select col1 from imp_tmp_tab t\r\n join jc_area a on a.id=t.col2\r\n  where col4 is  null)\r\n \r\n merge into user_register re using(\r\n select col1,a.cme_org_id from imp_tmp_tab t\r\n join jc_area a on a.id=t.col2\r\n  where col4 is  null) aa on (aa.col1=re.user_id)\r\n when matched then update set re.user_x_id=aa.cme_org_id ;\r\n\r\n   \r\n \r\n \r\n \r\n \r\n insert into imp_tmp_tab(col1,col2,col3)\r\n  select re.user_id,a.id,a.parenr_id  from icme_all_student st\r\n join icme_all_org o  on st.org_id=o.org_id\r\n join imp_tmp_tab t \r\n on o.org_code like   t.col6||\'%\'\r\n join user_register re on re.icme_ic_num=st.ic_code\r\n join jc_area a on a.cme_org_id=t.col2\r\n where  re.user_x_id<>t.col2;\r\n \r\n  \r\n insert into imp_tmp_tab(col1,col2,col3)\r\n  select re.user_id,a.id,a.parenr_id  from icme_all_student st\r\n join icme_all_org o  on st.org_id=o.org_id\r\n join imp_tmp_tab t \r\n on o.org_code like   t.col6||\'%\'\r\n join user_register re on re.icme_ic_num=st.ic_code\r\n join jc_area a on a.cme_org_id=t.col2\r\n where  re.user_x_id<>t.col2;\r\n \r\n select * from imp_tmp_tab where col4 is  null\r\n \r\n \r\n \r\n select *  from imp_tmp_tab where col4 is not  null\r\n and col6 is null \r\n \r\n \r\n \r\n \r\n \r\n \r\n merge into jc_user_extend e using(\r\n select col3,col4,col5 from imp_tmp_tab where col4 is not  null\r\n and col6 is null ) aa on (aa.col3=e.user_id)\r\n when matched then update set e.work_province=\'11\',\r\n   e.work_city=col4,\r\n   e.work_counties=aa.col5\r\n   \r\n   select * from jc_user_extend where  user_id in (\r\n select col1 from imp_tmp_tab where col4 is  null)', '1', '', '4', '90', '2', '89', '8', '58');
