INSERT INTO `daily_report_dba` VALUES ('167', '2018-01-24', '执行脚本', 'select *  from \r\nquestion_item_129 s where s.type=\'BTEST\'\r\nAND S.PARENTQUESTIONID=\'0\'\r\n\r\n\r\nselect *  from QUESTION_ANSWER\r\n\r\n\r\n\r\nA．唇红    <br>B．人中点<br>C．唇弓    <br>D．唇峰<br>E．唇珠\r\n\r\n\r\n \r\n \r\n\r\nupdate \r\nquestion_item_129 s\r\nset s.fronttitle=(select \r\n       listagg(ek.itemname||\'.\'||ek.content, \'<br>\') within GROUP(order by ek.questionid) answer\r\n\r\n  from QUESTION_ANSWER1 ek where\r\n ek.QuestionID=s.questionid\r\n\r\n group by ek.questionid)\r\n where s.type=\'BTEST\'\r\nAND S.PARENTQUESTIONID=\'0\';\r\n\r\n merge into question_item_129 s \r\n using ( select questionid, count(1) cnt  from  QUESTION_ANSWER1 ek group by ek.questionid )\r\n aa  on (aa.questionid=s.questionid)\r\n when matched then update set s.answer_num=aa.cnt;\r\n \r\n A.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D.[SB131.gif]I<br>E.[SB18.gif]FDG\r\n A.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D.卓家进家的小金橘很好吃[SB131.gif]I<br>E.[SB18.gif]FDG\r\n \r\nA.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D.[SB131.gif]I<br>E.[SB18.gif]FDG\r\nA.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D卓家进家的小金橘很好吃[SB131.gifI<br>E.[SB18.gifFDG\r\n\r\nA.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D.[SB131.gif]I<br>E.[SB18.gif]FDG\r\nA.碘海醇<br>B.泛影葡胺<br>C.注射用六氟化硫微泡（声诺维）<br>D.卓家进家的小金橘很好吃SB131.gifI<br>E.[SB18.gifFDG\r\nselect s.fronttitle,\r\n       instr(s.fronttitle, \'[\', 1),\r\n       replace(substr(s.fronttitle, 0, instr(s.fronttitle, \'[\', 1) - 1) ||\r\n       \'卓家进家的小金橘很好吃\' ||substr(s.fronttitle,  instr(s.fronttitle, \'[\', 1)+1), \']\', \'\')\r\n  from question_item_129 s\r\n where s.type = \'BTEST\'\r\n   AND S.PARENTQUESTIONID = \'0\'\r\n   and s.fronttitle like \'%[%gif%]%\'\r\n\r\n\r\n\r\n\r\n\r\n<p>各种肿瘤在颅内分布有其相对的特殊性，(43200145)所示的肿瘤首先考虑的诊断是</p><p><img galleryimg=\"no\" onload=\'javascript:if(this.width > 200)this.width = 200\' alt=\"\" src=\"http://examupload.haoyisheng.com/exam_upload/20111205/8b44f07d-b8bc-4cfd-ab1e-c4aada595014.JPG\" /></p>', '1', '', '219', '195', '2', '89', '50', '9');
INSERT INTO `daily_report_dba` VALUES ('168', '2018-01-31', '课件批量建架', '/*select * from   t_course  for update \r\nselect * from   t_ncourse */\r\n-- 插入课程\r\ninsert into t_ncourse\r\n  select \'2018\'||lpad(ncme_course_seq.nextval,8,\'0\'),\r\n         \'\',\r\n         a.c_name,\r\n         a.editor,\r\n         1,\r\n         sysdate,\r\n         0,\r\n         2,\r\n         a.mubiao,\r\n         \'\',\r\n         a.zhidao,\r\n         1,\r\n         1,\r\n         9,\r\n         0,\r\n         0,\r\n         a.c_sub,\r\n         \'\',\r\n         0,\r\n         0,\r\n         \'\',\r\n         \'\'\r\n    from (select a.c_name, a.c_sub,a.editor,a.mubiao,a.zhidao\r\n            from t_course a\r\n           group by a.c_name, a.c_sub,a.editor,a.mubiao,a.zhidao) a;\r\n--插入课件\r\n--select * from ncme_courseware \r\nselect * from  t_ncourseware\r\nupdate t_course xx set xx.expert_id=(select  exp_id from ncme_expert y where y.exp_name=xx.expert_id)\r\n\r\ninsert into t_ncourseware\r\n  select a.c_wid,\r\n         b.course_id,\r\n         a.cw_name,\r\n         a.expert_id,\r\n         \'\',\r\n         0,\r\n         sysdate,\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         \'\',\r\n         4,\r\n         0,\r\n         0,\r\n         0,\r\n         1,\r\n         0,\r\n         0,\r\n         \'\'\r\n    from t_course a, t_ncourse b\r\n   where a.c_name = b.name;\r\n\r\n-- 修改\r\nmerge into  t_ncourse t1 \r\nusing (\r\nselect course_id, count(*) c from t_ncourseware group by course_id) t2 on (t1.course_id=t2.course_id)\r\nwhen matched then\r\n  update set t1.cw_count=t2.c;\r\n\r\n-- select * from  t_que  for update ;\r\n/*select * from t_ncourse where name=\'全球新发和再发病媒传播疾病现状及防控\'\r\n select * from  t_que  where course_id=\'全球新发和再发病媒传播疾病现状及防控\'\r\n*/\r\n update   t_que x set course_id=(select y.course_id from t_ncourse y where trim(y.name)=trim(x.course_id))\r\n where question_id is  null;\r\nupdate t_que set question_id = ncme_question_seq.nextval where question_id is null;\r\n\r\n--insert into ncme_question select * from t_que;\r\n\r\n--插入课件库 t_kjkcourse\r\nselect * from   t_kjkcourse  \r\ninsert into t_kjkcourse  \r\nselect kjk_courseware_seq.nextval,\r\n       z.title,\r\n       x.c_name,\r\n       z.course_id || z.courseware_id,\r\n       \'\',\r\n       \'\',\r\n       13,\r\n       t.subject_name,\r\n       \'\',\r\n       \'\',\r\n       z_name,\r\n       z_danwei,\r\n       \'cme\',\r\n       sysdate,\r\n       sysdate,\r\n       sysdate,\r\n       0,\r\n       0,\r\n       \'\',\r\n       0,\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       13,\r\n       t.subject2_name,\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       0,\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\',\r\n       \'\'\r\n  from t_ncourseware z,\r\n       t_course x,\r\n       ncme_subject t\r\n where z.title = x.cw_name\r\n    and t.subject_id = x.c_sub;\r\nupdate t_ncourseware x set product_id=(select id from t_kjkcourse where name=x.title)\r\n\r\ninsert into ncme_course \r\nselect * from t_ncourse;\r\ninsert into ncme_courseware\r\nselect * from t_ncourseware;\r\ninsert into ncme_question\r\nselect * from  t_que;\r\ninsert into kjk_courseware  \r\nselect * from t_kjkcourse;', '1', '', '4', '90', '7', '89', '8', '212');
