INSERT INTO `daily_report_dba` VALUES ('173', '2018-01-31', '执行脚本', 'update sip_auth_user sa set sa.dept_code1=\'北京乡医\' where sa.id in(\r\nselect distinct t1.id --3995\r\n  from sip_auth_user t1\r\n  join sip_auth_user_extend t2\r\n    on t1.id = t2.user_id\r\n   join sip_auth_org_user_tree t3\r\n    on t1.id = t3.user_id -- 这是科室\r\n  join sip_auth_org_map t8\r\n    on t8.id = t3.map_id\r\n  left join sip_auth_org_tree t4\r\n    on t3.org_id = t4.ORG_ID\r\n   and t8.id = t4.map_id\r\n  left join sip_auth_org_info tt\r\n    on tt.id = t3.org_id\r\n  left join sip_auth_org_info t5\r\n    on t4.ORG_PARENT_ID = t5.id\r\n  left join sip_auth_org_tree tr\r\n    on tr.org_id = t5.id\r\n   and t8.id = tr.map_id\r\n  left join sip_auth_user_base t6\r\n    on t1.id = t6.user_id\r\n   and t8.service_id = t6.service_id\r\n   and t8.site_id = t6.site_id\r\n  left join SIP_BASE_HOSPITAL_VALUE t7\r\n    on t6.base_id = t7.id\r\n  left join co_dic_data t9\r\n    on t2.highest_record_school = t9.id\r\n  left join co_dic_data td\r\n    on t2.sex = td.id\r\n  left join co_dic_data td1\r\n    on t2.highest_record_school = td1.id\r\n  left join co_dic_data td2\r\n    on t2.title = td2.id\r\n  left join co_dic_data td3\r\n    on t2.specialty_id = td3.id\r\n where t1.user_account in (\'520013995412304016\',\r\n                           \'52004928\'))', '1', '', '81', '5', '2', '89', '34', '9');
INSERT INTO `daily_report_dba` VALUES ('174', '2018-02-01', 'cme数据年度结转。', '--0.备份之前先执行Job程序,然后进行数据备份----3分钟\r\ncall ncme_build_order_number();\r\ncall ncme_build_province_serial();\r\ncall ncme_sd_serail();\r\n    \r\n    --备份数据库到:/backup/datapump 目录   约 20 分钟\r\n  --  expdp system/R2DZSJ9TKB2P0A31W  full=y directory=datapump dumpfile=ncme_20150201.dmpdp logfile=ncme_20150201.log  compression=all\r\n\r\n   --1.清理操作记录========== 2 分钟\r\n\r\n       ---11 删除去年的操作日志\r\n       /*前提条件：无 */\r\n       delete from ncme_manage_log where func<>\'会员管理\' and to_char(logtime,\'yyyy\') < ncme_getapplyyear(sysdate)-1;\r\n\r\n       ---1.2 更新免费赠送学习卡,改为不赠送\r\n       update ncme_admin_organ set card_type=0;\r\n\r\n       ---1.3 删除制卡记录（保留一年的记录）\r\n       delete from ncme_made_list t where to_char(made_date,\'yyyy\')< ncme_getapplyyear(sysdate)-1;\r\n       \r\n       ---1.4 删除存贮过程执行记录（保留7天的记录）\r\n       delete from job_log where trunc(sysdate-job_time)>7; \r\n       \r\n   analyze table ncme_manage_log estimate statistics sample 40 percent;\r\n    analyze table ncme_made_list estimate statistics sample 40 percent;\r\n commit;           \r\n    \r\n\r\n   --2.课程结转================== 4 分钟\r\n       ---2.1 更新课程点击率\r\n       /*前提条件：无*/\r\n       update ncme_course c set c.total_click_times=c.total_click_times + c.click_times;\r\n       update ncme_course c set c.click_times=0;\r\n       \r\n       ---2.2.将没有授权的课程下线\r\n       /*前提条件：无\r\n       */\r\n       update ncme_course c set publish_flag=2,c.publish_date=sysdate \r\n       where publish_flag=1\r\n         and not exists (select 1 from ncme_course_credit e \r\n                         where e.course_id=c.course_id \r\n                           and credit_year=ncme_getapplyyear(sysdate));\r\n                           \r\n       update ncme_course c set publish_flag=1,c.publish_date=sysdate \r\n       where publish_flag<>1\r\n         and  exists (select 1 from ncme_course_credit e \r\n                         where e.course_id=c.course_id \r\n                           and credit_year=ncme_getapplyyear(sysdate));\r\n                           \r\n                           \r\n       ---2.3. 将过期课程的授权添加到历史表中\r\n       /*前提条件：2月1日最先处理，然后做课程授权\r\n       */\r\n       insert into ncme_course_credit_history(credit_year,course_id,course_name,organ_id,organ_name,course_type,credit_type,credit_num,evp_value,course_serial,re_study_flag,credit_date,request)\r\n       select cc.credit_year,cc.course_id,c.name as coursename,cc.organ_id,o.name as organname,cc.course_type,cc.credit_type,credit_num,evp_value,course_serial,re_study_flag,credit_date,request \r\n       from ncme_course_credit cc\r\n       left join ncme_course c on cc.course_id=c.course_id\r\n       left join ncme_admin_organ o on cc.organ_id=o.organ_id\r\n       where cc.credit_year <= ncme_getapplyyear(sysdate)-1;\r\n        \r\n       ---2.4 删除过期课程的授权\r\n       /*前提条件：2.3执行成功\r\n       */\r\n       delete from ncme_course_credit where credit_year <=ncme_getapplyyear(sysdate)-1;\r\n       \r\n       ---2.5 删除下线课程的意见反馈\r\n       delete from ncme_course_feedback f\r\n       where not exists (select 1 from ncme_course c where c.course_id=f.course_id and c.publish_flag=1);\r\n\r\n       ---2.6 删除下线课程的课件交流信息\r\n       delete from ncme_courseware_comm f\r\n       where not exists (select 1 from ncme_course c where c.course_id=f.course_id and c.publish_flag=1);\r\n       \r\n      ---2.7 删除已下线项目的收藏夹\r\n       delete from ncme_course_favorite t\r\n       where not exists (select 1 from ncme_course s where s.course_id=t.course_id  and publish_flag=1);\r\n         \r\n       ---2.9.对课程授权表进行分析优化\r\n       /*前提条件：2.4执行成功\r\n       */\r\n   analyze table ncme_course_credit compute statistics ;\r\n     analyze table ncme_course_feedback estimate statistics sample 40 percent ;\r\n    analyze table ncme_courseware_comm estimate statistics sample 40 percent ;\r\n     analyze table ncme_course_favorite estimate statistics sample 40 percent ;\r\n       commit;           \r\n\r\n\r\n--3.学习记录结转============80 分钟\r\n \r\n       --3.1.检查生成ncme_study_credit的order_numberf编号\r\n       /*前提条件：无*/        \r\n\r\n       \r\n       ---3.2 删除无效的学习卡订单\r\n       /*前提条件：无*/\r\n       delete ncme_studycard_order where status=0 and order_time < sysdate-7; \r\n       \r\n       ---3.3 结转已过期学习卡    \r\n       /*前提条件：2月1日，等证书组处理完数据后再操作  */\r\n       delete from ncme_study_card_history h where exists (select 1 from ncme_study_card c where c.card_no=h.card_no);\r\n       \r\n       insert into ncme_study_card_history\r\n       select * from ncme_study_card c\r\n       where EXISTS (select 1 from ncme_pay_card p where c.card_type=p.card_type AND end_date<SYSDATE)\r\n         AND NOT EXISTS ( SELECT 1 FROM sd_ncme_pay_card s WHERE c.card_type=s.card_type AND end_date > SYSDATE);\r\n       \r\n       ---3.4 删除已过期学习卡  \r\n        /*前提条件：3.3执行成功\r\n        */     \r\n       DELETE from ncme_study_card c\r\n       where EXISTS (select 1 from ncme_pay_card p where c.card_type=p.card_type AND end_date<SYSDATE)\r\n         AND NOT EXISTS ( SELECT 1 FROM sd_ncme_pay_card s WHERE c.card_type=s.card_type AND end_date > SYSDATE);\r\n          \r\n       ---3.5 更新山东卡类型的可学课程数为2   \r\n       UPDATE ncme_study_card SET times=2\r\n       WHERE card_type IN(SELECT card_type FROM sd_ncme_pay_card);    \r\n           \r\n       ---3.6 删除已下线项目的学习记录    \r\n       /*前提条件：先确认医学编辑该下线的课程已下线，该上线的课程已上线,但保留淄博和深圳平台的学习记录\r\n       另外：保留一年的学习记录\r\n       */\r\n       delete from ncme_study_course s \r\n       where not exists (select 1 from ncme_course c where s.course_id=c.course_id and c.publish_flag=1)\r\n         and trunc(s.startdate)< add_months(trunc(sysdate,\'mm\'),-12)\r\n         and trunc(s.passdate)< add_months(trunc(sysdate,\'mm\'),-12)\r\n         and length(s.user_name)<36 \r\n         and s.user_name not like \'zibo-%\'\r\n         and s.user_name not like \'jinan-%\'\r\n         and s.user_name not like \'weihai-%\'\r\n         and s.user_name not like \'xjbt-%\';\r\n\r\n       ---3.7 删除已下线项目的课件学习记录    \r\n       /*前提条件：2.5执行成功*/   \r\n       delete from ncme_study_quiz q\r\n       where not exists (select 1 from ncme_study_course s where s.course_id=q.course_id);\r\n \r\n       ---3.8 结转已付费记录    \r\n       /*前提条件：先确认证书组将所有证书都批改完毕（senddate is null条件记录是0），\r\n                \r\n       */\r\n       insert into ncme_study_credit_history\r\n       select * from ncme_study_credit s\r\n       where s.apply_year <= ncme_getapplyyear(sysdate)-1;\r\n\r\n       ---3.9 删除已付费记录\r\n       /*前提条件：3.8执行成功   */\r\n       \r\n       -- Create table  注意修改索引名称， 建表语句 见备注\r\n\r\n\r\n       \r\nrename NCME_STUDY_CREDIT to NCME_STUDY_CREDIT_bak1;\r\nrename NCME_STUDY_CREDIT_bak to NCME_STUDY_CREDIT;\r\ninsert into NCME_STUDY_CREDIT \r\n select * from  NCME_STUDY_CREDIT_bak1 s \r\n       where s.apply_year > ncme_getapplyyear(sysdate)-1; \r\ncommit;          \r\n--  表NCME_STUDY_CREDIT_bak1 可酌情删除     \r\n       \r\n  ---3.10.对表进行分析优化\r\n       /*前提条件：3.9执行完毕\r\n       */\r\n      analyze table ncme_study_course compute statistics;\r\n      analyze table ncme_study_quiz compute statistics;\r\n      analyze table ncme_study_credit compute statistics;\r\n      analyze table ncme_study_card estimate statistics sample 40 percent;\r\n      analyze table ncme_studycard_order estimate statistics sample 40 percent;\r\n      analyze table ncme_study_credit_history compute statistics  sample 40 percent;', '1', 'create table NCME_STUDY_CREDIT_bak\r\n(\r\n  USER_NAME       VARCHAR2(50) not null,\r\n  COURSE_ID       VARCHAR2(12) not null,\r\n  ORGAN_ID        VARCHAR2(12) not null,\r\n  APPLY_YEAR      INTEGER not null,\r\n  APPLYDATE       DATE default SYSDATE not null,\r\n  STARTDATE       DATE,\r\n  PASSDATE        DATE,\r\n  APPLY_TYPE      VARCHAR2(24) not null,\r\n  APPLY_SUBTYPE   VARCHAR2(24),\r\n  CARD_NO         VARCHAR2(16),\r\n  APPLY_CREDIT    FLOAT,\r\n  ICME_MARK       DATE,\r\n  SENDDATE        DATE,\r\n  OPERATOR        VARCHAR2(24),\r\n  SERIAL_NUMBER   VARCHAR2(200),\r\n  ANSWERTIMES     INTEGER,\r\n  CREDIT_TYPE     VARCHAR2(20),\r\n  COURSE_SERIAL   VARCHAR2(128),\r\n  ORDER_NUMBER    VARCHAR2(24),\r\n  USER_X_ID       VARCHAR2(9),\r\n  IC_CODE         VARCHAR2(11),\r\n  ICME_SUBJECT_ID INTEGER default 0,\r\n  POOL_ID         VARCHAR2(12),\r\n  OLD_USER        VARCHAR2(24),\r\n  HY_FLAG         INTEGER default 0 not null,\r\n  HY_FAIL_CAUSE   VARCHAR2(64),\r\n  IMPORT_MARK     VARCHAR2(1000)\r\n)\r\ntablespace NCME\r\n  pctfree 10\r\n  initrans 1\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64\r\n    next 8\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\n-- Create/Recreate primary, unique and foreign key constraints \r\nalter table NCME_STUDY_CREDIT_bak\r\n  add primary key (USER_NAME, COURSE_ID, ORGAN_ID, APPLY_YEAR)\r\n  using index \r\n  tablespace NCME\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 10M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\n-- Create/Recreate indexes \r\ncreate index IDX_F_STUDY_SYNC on NCME_STUDY_CREDIT_bak (DECODE(TO_CHAR(ICME_MARK,\'YYYY-MM-DD HH24:MI:SS\'),NULL,0,NULL), USER_NAME)\r\n  tablespace NCME\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index IDX_N_STUDY_CREDIT_USERXID on NCME_STUDY_CREDIT_bak (USER_X_ID)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index IDX_STUDY_CREDIT_CME2ICME on NCME_STUDY_CREDIT_bak (ICME_MARK, USER_NAME, APPLY_CREDIT)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index IDX_STU_CRE_ICC on NCME_STUDY_CREDIT_bak (IC_CODE)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index IND_CARD_TYPE0 on NCME_STUDY_CREDIT_bak (CARD_NO, CREDIT_TYPE)\r\n  tablespace NCME\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index IND_STUDY_CREDIT_APPLYDATE on NCME_STUDY_CREDIT_bak (APPLYDATE, USER_X_ID)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index I_STU_POOL_ID on NCME_STUDY_CREDIT_bak (POOL_ID)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index XPKNCME_STUDY_CREDIT_ORDER on NCME_STUDY_CREDIT_bak (ORDER_NUMBER)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 64K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\ncreate index XPKNCME_STUDY_CREDIT_SENDDATE on NCME_STUDY_CREDIT_bak (SENDDATE)\r\n  tablespace USERS\r\n  pctfree 10\r\n  initrans 2\r\n  maxtrans 255\r\n  storage\r\n  (\r\n    initial 128K\r\n    next 1M\r\n    minextents 1\r\n    maxextents unlimited\r\n  );\r\n-- Grant/Revoke object privileges \r\ngrant select, insert, update, delete on NCME_STUDY_CREDIT_bak to CME;', '4', '90', '7', '214', '8', '9');
INSERT INTO `daily_report_dba` VALUES ('175', '2018-02-01', '修改单学科地区授权', '授权的时候烦请先将国家级项目课题ID：201600919102 单学科授权地区进行调整（地区为：北京、天津、河北、安徽、江苏、新疆、浙江),然后进行批量授权。\r\n  非授权项目授权的时候注意先将课题ID：201700921424 单学科授权地区进行调整（地区为：北京、天津、河北、安徽、江苏、新疆、浙江),然后进行批量授权。\r\nselect * from ncme_course_credit where course_id=\'201600919102\' \r\n\r\nupdate ncme_course_credit a\r\n   set a.is_credit_subject = 0\r\n where a.course_id in  (\'201600919102\',\'201700921424\')\r\n   and organ_id in (select organ_id\r\n                      from ncme_admin_organ na  \r\n                     where na.district_id like \'10%\'\r\n                        or na.district_id like \'11%\'\r\n                        or na.district_id like \'12%\'\r\n                        or na.district_id like \'20%\'\r\n                        or na.district_id like \'45%\'\r\n                        or na.district_id like \'46%\'\r\n                        or na.district_id like \'23%\')', '1', '', '4', '90', '7', '214', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('176', '2018-02-01', '保留17年部分授权', 'insert into Ncme_Course_Credit\r\nselect COURSE_ID         ,                                      \r\nORGAN_ID          ,                                      \r\nCREDIT_TYPE       ,                                      \r\nCREDIT_NUM        ,                                      \r\nEVP_VALUE         ,                                      \r\nCOURSE_SERIAL     ,                                      \r\nRE_STUDY_FLAG     ,\r\nCREDIT_DATE       ,                                      \r\nCOURSE_TYPE       ,\r\nCREDIT_YEAR       ,                                      \r\nREQUEST           ,                                      \r\nIS_CREDIT_SUBJECT ,                                      \r\nCARD_TYPE         ,                                      \r\nICME_SUBJECT_ID   from Ncme_Course_Credit_History a \r\nwhere a.credit_type=\'PX\' and a.credit_year=2017 \r\nand course_id=\'201700921549\';\r\ninsert into Ncme_Course_Credit\r\nselect  COURSE_ID         ,                                      \r\nORGAN_ID          ,                                      \r\nCREDIT_TYPE       ,                                      \r\nCREDIT_NUM        ,                                      \r\nEVP_VALUE         ,                                      \r\nCOURSE_SERIAL     ,                                      \r\nRE_STUDY_FLAG     ,\r\nCREDIT_DATE       ,                                      \r\nCOURSE_TYPE       ,\r\nCREDIT_YEAR       ,                                      \r\nREQUEST           ,                                      \r\nIS_CREDIT_SUBJECT ,                                      \r\nCARD_TYPE         ,                                      \r\nICME_SUBJECT_ID from Ncme_Course_Credit_History a \r\nwhere a.credit_type=\'P2\' and a.credit_year=2017\r\nand organ_id =\'000000000008\';\r\ninsert into Ncme_Course_Credit\r\nselect  COURSE_ID         ,                                      \r\nORGAN_ID          ,