INSERT INTO `daily_report_dba` VALUES ('194', '2018-02-06', '房山数据掉漆', 'select * from   imp_tmp_tab for update \r\n\r\n\r\n83176048011-83176049004\r\nupdate imp_tmp_tab set col3=substr(col1,-11);\r\n\r\nupdate imp_tmp_tab set col2=substr(col1,0,11);\r\n\r\n\r\nselect ca.card_no,t.col1, re.user_real_name,      user_getdistrict(re.user_x_id, 1) 省,\r\n         user_getdistrict(re.user_x_id, 2) 市,\r\n         user_getdistrict(re.user_x_id, 3) 县 from ncme_study_card_history ca join imp_tmp_tab t\r\non ca.card_no>=t.col2\r\nand ca.card_no<=t.col3\r\nleft join user_register re on re.login_name=ca.user_name\r\n\r\n\r\nmerge into imp_tmp_tab tt using (\r\nselect h.user_name ,min(h.startdate) startdate from ncme_study_credit_history h where h.apply_year=\'2017\'\r\nand h.user_name in(select col3 from imp_tmp_tab)\r\ngroup by h.user_name )\r\naa on(aa.user_name=tt.col3)\r\nwhen matched then update set tt.col13=aa.startdate\r\n select t.col1,t.col2,col3,col6,col7,col13,re.icme_ic_num,      case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别 from imp_tmp_tab t \r\n join user_register re on re.login_name=t.col3', '1', '', '4', '90', '2', '91', '8', '58');
INSERT INTO `daily_report_dba` VALUES ('195', '2018-02-06', '安徽市级II类授权', 'select col1 from  imp_tmp_tab group by col1 having count(1) >1\r\n\r\n\r\nupdate  imp_tmp_tab set col1=trim(col1) where col1 like \'% %\'\r\n\r\n\r\n\r\nselect* from imp_tmp_tab for update \r\ndelete from imp_tmp_tab where col6 is null\r\nupdate  imp_tmp_tab set col1=trim(col1)\r\n\r\nselect* from   ncme_course cc WHERE   name IN(\r\nselect T.COL2 from ncme_course cc join imp_tmp_tab t on t.col2=cc.name\r\ngroup by T.COL2 HAVING count(*) >1) \r\n\r\n\r\n\r\nselect* from  imp_tmp_tab WHERE  col2 IN(\r\nselect T.COL2 from ncme_course cc join imp_tmp_tab t on t.col2=cc.name\r\ngroup by T.COL2 HAVING count(*) >1) for update \r\n\r\nmerge into imp_tmp_tab t \r\nusing ncme_course cc on (cc.name=t.col2)\r\nwhen matched then \r\n  update set t.col6=cc.course_id\r\n\r\n                                                                                                                             \r\n           --cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab;                                                                                  \r\n                                                                                                                             \r\n           delete  imp_tmp_tab ;                                                                                       \r\n                       select* from ncme_course_credit c  where c.course_id=\'201600919338\'\r\n                        and c.course_serial=\'AH-2018-001\'                                                                                                       \r\n           insert into ncme_course_credit                                                                                    \r\n           select distinct  t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           t.col13,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n           where c.course_id=\'201600919338\'\r\n                        and c.course_serial=\'AH-2018-001\' \r\n                        and t.col1<>\'#N/A\'\r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )                                                  \r\n                                                                                                                  \r\n     select* from (\r\nselect t.col6,cs.organ_id,cs.credit_year   from ncme_course_credit cs ,imp_tmp_tab t \r\n where cs.course_id=\'201600919210\' and cs.course_serial=\'2016-003-HYS\'                                                                    \r\n) ts,(select css.course_id,css.organ_id,css.credit_year   from ncme_course_credit css )ts1\r\nwhere ts.col6=ts1.course_id\r\nand     ts.organ_id=ts1.organ_id                                                        \r\n       \r\n           \r\n  \r\n  \r\n  \r\n  \r\n                                                                                                                          \r\nselect * from ncme_course cg where cg.course_id in( select col1 from imp_tmp_tab) and cg.publish_flag<>1', '1', '', '4', '90', '2', '89', '8', '58');
INSERT INTO `daily_report_dba` VALUES ('196', '2018-02-06', '湛江数据调取', '--导出cme数据\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,re.login_passwd,\r\n        re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n      case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n    sc.card_no 付费卡号,\r\n      nc.name 课程名称,\r\n sc.apply_subtype 申请子类型,\r\nsc.startdate 开始日期,\r\n sc.passdate 通过日期,\r\n sc.applydate 申请日期,\r\n\r\n sc.apply_credit  学分,\r\n sc.ic_code,\r\n sc.course_serial\r\n     -- sc.apply_credit*3 学时,\r\n      --ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where sc.course_id in (\'201700921665\',\'201700921664\'\r\n                       )\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名, re.login_passwd,\r\n        re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n       null 付费卡号,\r\n       nc.name 课程名称,\r\n       null 申请子类型,\r\n       sc.startdate 开始日期,\r\n       sc.passdate 通过日期,\r\n      null 申请日期,   null 学分,\r\n\'\',\r\n\'\'\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where sc.course_id in (\'201700921665\',\'201700921664\'\r\n                       )\r\n                       and passdate is not null\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\nand sc.startdate<to_date(\'2018-2-1\',\'yyyy-mm-dd\')', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('197', '2018-02-06', '北京II类添加学时备注以及icme学时对应', 'select * from  imp_tmp_tab for update ;\r\nupdate imp_tmp_tab set col2=trim(col2);\r\nselect distinct request from ncme_course_credit cs where/* cs.course_id in(\r\nselect col1 from imp_tmp_tab\r\n)and*/ cs.credit_year=\'2018\'\r\nand cs.request like \'%学时%\'\r\nand cs.request is  null\r\nand   cs.credit_type=\'R2\';\r\n\r\nmerge into ncme_course_credit cs \r\nusing imp_tmp_tab t \r\non (cs.course_id=t.col1)\r\nwhen matched then update set cs.request=t.col2||\'学时\'\r\n  where   cs.credit_year=\'2018\'\r\nand cs.request is  null\r\nand   cs.credit_type=\'R2\';\r\n\r\ninsert into  cme2icme_scoresync_bjhour \r\nselect col2,col1 from imp_tmp_tab@ncme tt\r\nwhere not exists (\r\nselect 1 from  cme2icme_scoresync_bjhour bb\r\nwhere bb.hour=tt.col2 and bb.course_id=tt.col1\r\n)', '1', '', '6', '115', '2', '97', '8', '58');
