INSERT INTO `daily_report_dba` VALUES ('205', '2018-02-07', '烦请调取四川大竹县2016年，2017年的CME学习数据，数据单一会给你，谢谢！\r\n数据字段为：区县、单位、姓名、科室、职称、课题、学分、学时、学习时间', 'select * from user_district d where d.district_name like \'%大竹%\'\r\n\r\n--导出cme数据\r\nselect sc.course_id,apply_year,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n      \r\n\r\n       re.user_real_name 姓名,\r\n      \r\n       re.user_hsp_name 单位,\r\n   \r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n     \r\n      nc.name 课程名称,\r\nsc.applydate 申请日期,\r\nsc.apply_credit  学分,\r\nct.credit_name 学分类型\r\n  from ncme_study_credit_history sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where    sc.user_x_id like \'370153434%\'\r\n and sc.apply_year in(\'2016\',\'2017\')', '1', '', '4', '90', '2', '97', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('206', '2018-02-07', '上线脚本', '-- Create table\r\ncreate table ZYY_FUND_MANAGE_TYPE\r\n(\r\n  id          NUMBER(18) not null,\r\n  type_name   VARCHAR2(50) not null,\r\n  type        NUMBER(18) not null,\r\n  is_delete   NUMBER(1),\r\n  create_date DATE,\r\n  creater     NUMBER(18),\r\n  zyy_org_id  NUMBER(18),\r\n  PRIMARY KEY (ID)\r\n);\r\n\r\n\r\n\r\n-- Create table\r\ncreate table ZYY_FUND_MANAGE_DETAIL\r\n(\r\n  id             NUMBER(18) not null,\r\n  manage_id      NUMBER(18),\r\n  manage_type_id NUMBER(18),\r\n  money          NUMBER(18,2),\r\n  content        VARCHAR2(1000),\r\n  PRIMARY KEY (ID)\r\n);\r\nalter table ZYY_FUND_MANAGE_DETAIL\r\n  add constraint FUND_MANAGE_DETIAL_TYPE_FK foreign key (MANAGE_TYPE_ID)\r\n  references ZYY_FUND_MANAGE_TYPE (ID);\r\n  \r\n  \r\n\r\n-- Create table\r\ncreate table ZYY_FUND_MANAGE\r\n(\r\n  id             NUMBER(18) not null,\r\n  name           VARCHAR2(50),\r\n  manage_type_id NUMBER(18),\r\n  manage_date    DATE,\r\n  type           NUMBER(18),\r\n  create_date    DATE,\r\n  creater        NUMBER(18),\r\n  picture        BLOB,\r\n  picture_name   VARCHAR2(200),\r\n  PRIMARY KEY (ID)\r\n);\r\nalter table ZYY_FUND_MANAGE\r\n  add constraint FUND_MANAGE_TYPE_ID_FK foreign key (MANAGE_TYPE_ID)\r\n  references ZYY_FUND_MANAGE_TYPE (ID);\r\n  \r\n  \r\n-- Create table\r\ncreate table ZYY_SALARY_MANAGE\r\n(\r\n  id               NUMBER(18) not null,\r\n  certificate_no   VARCHAR2(255),\r\n  pay_month        DATE,\r\n  pay_date         DATE,\r\n  pay_total_amount NUMBER(18),\r\n  zyy_user_id      NUMBER(18) not null,\r\n  status           INTEGER,\r\n  last_update_date DATE,\r\n  PRIMARY KEY (ID)\r\n);\r\nCREATE SEQUENCE ZYY_FUND_MANAGE_TYPE_SEQ\r\nINCREMENT BY 1 \r\nSTART WITH 1 \r\nNOMAXVALUE \r\nNOCYCLE \r\nCACHE 10;\r\nCREATE SEQUENCE ZYY_FUND_MANAGE_DETAIL_SEQ\r\nINCREMENT BY 1 \r\nSTART WITH 1 \r\nNOMAXVALUE \r\nNOCYCLE \r\nCACHE 10;\r\nCREATE SEQUENCE zyy_fund_manage_seq\r\nI