INSERT INTO `daily_report_dba` VALUES ('210', '2018-02-08', '拆分字段行转列', 'select * from id_relation_tmp\r\n\r\n\r\n\r\n-- Create table\r\ncreate table id_relation_tmp\r\n(\r\n  id  NVARCHAR2(32),\r\n  ids  VARCHAR2(4000)\r\n)\r\n\r\ninsert into id_relation_tmp\r\nselect * from id_relation;\r\ncreate table id_relation_rep\r\nas\r\n\r\nSELECT\r\n    ID,\r\n    REGEXP_SUBSTR (ids, \'[^,]+\', 1, lv) ids\r\nFROM\r\n    id_relation_tmp t,\r\n    (\r\n        SELECT\r\n            LEVEL lv\r\n        FROM\r\n            dual CONNECT BY LEVEL < 10\r\n    ) b\r\nWHERE\r\n    b.lv <= REGEXP_COUNT (t.ids, \'\\,\') + 1\r\nORDER BY\r\n ID;', '1', '', '219', '195', '2', '97', '50', '9');
INSERT INTO `daily_report_dba` VALUES ('211', '2018-02-08', '批量插入考题', '--select ncme_question_seq.nextval from dual\r\n\r\nselect *\r\n  from T_QUEstion\r\n   for update\r\n\r\nupdate T_QUEstion set course_id = trim(course_id), courseware_id = trim(courseware_id);\r\nupdate T_QUEstion set question_id = ncme_question_seq.nextval;\r\n\r\nupdate T_QUEstion x\r\n   set answcont =\r\n       (select \'A.\' || answcont1 || \'<br>B.\' || answcont2 || \'<br>C.\' ||\r\n               answcont3 || \'<br>D.\' || answcont4 || case\r\n                 when answcont5 is not null then\r\n                  \'<br>E.\' || answcont5\r\n               end || case\r\n                 when answcont6 is not null then\r\n                  \'<br>F.\' || answcont6\r\n               end\r\n          from T_QUEstion a\r\n         where x.question_id = a.question_id)\r\n where answcont is null;\r\n\r\ndeclare\r\n  v_cnt INT;\r\n  cursor c1 is\r\n    select * from T_QUEstion;\r\nbegin\r\n  for r in c1 loop\r\n    v_cnt := 0;\r\n    if r.answcont1 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    if r.answcont2 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    if r.answcont3 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    if r.answcont4 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    if r.answcont5 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    if r.answcont6 is not null then\r\n      v_cnt := v_cnt + 1;\r\n    end if;\r\n    update T_QUEstion\r\n       set answ_num = v_cnt\r\n     where question_id = r.question_id;\r\n  \r\n  end loop;\r\nend;\r\n\r\ninsert into ncme_question\r\n  select QUESTION_ID,\r\n         COURSE_ID,\r\n         COURSEWARE_ID,\r\n         ANSW_NUM,\r\n         QUESCONT,\r\n         ANSWCONT,\r\n         ANSWER,\r\n         INTERPRET\r\n    from T_QUEstion;', '1', '', '4', '90', '7', '214', '8', '212');
