INSERT INTO `daily_report_dba` VALUES ('215', '2018-02-08', '官网数据向app_op覆盖', '--官网数据copy到.3  gw_opp\r\nexpdp system/oracle DIRECTORY=DATAPUMP schemas=hysgw DUMPFILE=gw0208.dmp    logfile=gw0208.log   compression=all\r\n--清理数据 gw_opp\r\ndrop table cms_app_version\r\ndrop table cms_index_static;\r\ndrop table cms_parameter;\r\ndrop table cms_role;\r\ndrop table cms_role_permission;\r\ndrop table cms_user;\r\ndrop table cms_user_role;\r\ndrop table yyq_dict_category;\r\ndrop table yyq_dict_value;\r\ndrop table cms_permission;\r\ndrop table yyq_blog;\r\n--app_op备份yyq_blog表\r\nselect * from yyq_blog\r\ncreate table   yyq_blog_bak\r\nas select * from  yyq_blog;\r\n\r\n\r\n--全库备份app_op\r\nexpdp system/1qaz2wsx DIRECTORY=DATAPUMP schemas=app_op DUMPFILE=app_op0208.dmpdp   logfile=app_op.log\r\n  \r\n--导入gw_op至app_op\r\nimpdp system/oracle DIRECTORY=DATAPUMP schemas=gw_opp remap_schema=gw_opp:app_op DUMPFILE=gw_opp.dmpdp   logfile=gw_opp.log table_exists_action=replace\r\n--恢复 yyq_blog表 app_op\r\ninsert into yyq_blog\r\nselect * from yyq_blog_bak', '1', '', '208', '213', '2', '223', '50', '9');
INSERT INTO `daily_report_dba` VALUES ('216', '2018-02-08', '商务会员数据调取', 'select \r\n         user_getdistrict(re.user_x_id, 1) 省,\r\n         user_getdistrict(re.user_x_id, 2) 市,\r\n         user_getdistrict(re.user_x_id, 3) 县,\r\n         user_getuserinfo(re.login_name, 4) 医院级别,\r\n         \r\n         User_getUserInfo(re.login_name, 1) 科室,\r\n      User_getUserInfo(re.login_name, 2) 职称,count(1) \r\n      \r\n          from user_register re where re.dept_id in(\r\nselect d.dept_id from user_dept_define d where d.dept_name like \'%放射%\'\r\nand d.dept2_id not in(\'48\')\r\nunion\r\n\r\nselect d.dept_id from user_dept_define d where d.dept_name like \'%影像医学%\'\r\nunion\r\n\r\nselect d.dept_id from user_dept_define d where d.dept_name like \'%肿瘤%\'\r\nand d.dept2_id not in(\'66\')\r\nunion\r\n\r\nselect d.dept_id from user_dept_define d where d.dept_name like \'%放疗%\'\r\nunion \r\nselect d.dept_id from user_dept_define d where d.dept_name like \'%血液%\'and d.dept2_id not in(\'23\',\'46\'))\r\n\r\ngroup by \r\n\r\n         user_getdistrict(re.user_x_id, 1) ,\r\n         user_getdistrict(re.user_x_id, 2) ,\r\n         user_getdistrict(re.user_x_id, 3) ,\r\n         user_getuserinfo(re.login_name, 4) ,\r\n         \r\n         User_getUserInfo(re.login_name, 1) ,\r\n      User_getUserInfo(re.login_name, 2)', '1', '', '4', '90', '2', '91', '8', '63');
