INSERT INTO `daily_report_dba` VALUES ('219', '2018-02-09', '修改数据', 'update nproject_project np set np.checkstatus=-1,np.gongbu=1 \r\nwhere  np.checkstatus = 0\r\n   and np.gongbu = 0\r\n  -- and np.report = 0\r\n   and np.year = 2018\r\n   and np.beian = 0\r\n   and np.edit = 1\r\n   and np.check_org_id =\r\n       (select ns.id from nproject_sysuser ns where ns.username = \'010\');', '1', '', '66', '95', '7', '89', '36', '9');
INSERT INTO `daily_report_dba` VALUES ('220', '2018-02-09', '修改 数据', '--1.拼数据\r\ndeclare\r\n\r\n  cursor c1 is\r\n    select ne.project_id, ne.convention_option, ne.opinion\r\n      from xmsb_bj.nproject_project np, xmsb_bj.nproject_evaluation ne\r\n     where np.id = ne.project_id\r\n       and np.checkstatus = -1\r\n       and np.gongbu = 1\r\n       and np.year = 2018\r\n       and ne.result = 0\r\n       and ne.final <> 1\r\n       and np.id not in (15515, 15691, 15802);\r\n\r\nbegin\r\n  for r in c1 loop\r\n    insert into t_nproject_evaluation\r\n      select r.project_id, wm_concat(value), r.opinion\r\n        from nproject_evaluation_opinion\r\n       where id in\r\n             (\r\n              \r\n              SELECT DISTINCT REGEXP_SUBSTR(r.convention_option,\r\n                                             \'[^,]+\',\r\n                                             1,\r\n                                             LEVEL) as \"token\"\r\n                FROM DUAL\r\n              CONNECT BY REGEXP_SUBSTR(r.convention_option, \'[^,]+\', 1, LEVEL) IS NOT NULL);\r\n  \r\n  end loop;\r\nend;\r\n\r\n\r\n\r\n--2.插数据\r\ninsert into nproject_approval\r\nselect nproject_approval_seq.nextval,project_id,\'2\',0,sysdate,value||case when option1 is not null\r\nthen \',\' end \r\n||option1 as value2 from t_nproject_evaluation ;\r\n--3.改逗号开头\r\nupdate   nproject_approval set opinion=substr(opinion,2,length(opinion)-1) where opinion like \',%\' and org_id=2;', '1', '', '66', '95', '7', '214', '36', '9');
INSERT INTO `daily_report_dba` VALUES ('221', '2018-02-11', '补充 pxpt_report_result 数据', 'insert   into  report_result_studyLog\r\n select r.id,\r\n       r.account_name , --登陆名\r\n       r.real_name, --姓名\r\n       r.areaname,\r\n       r.orgname, --单位名称\r\n       r.CULTIVATE_BATCH_NAME,\r\n       r.sex,\r\n       r.mobilnumber,\r\n       r.certificate_no,\r\n       r.aliasname,\r\n       r.project_id,\r\n       r.project_name, --项目名称\r\n       pcc.cware_name,  --课程名称\r\n       pcc.cware_id,\r\n       pcc.course_class_hour, --学时\r\n       psl.study_state,\r\n       psl.one_time,\r\n       psl.two_time,\r\n       psl.study_date,\r\n       psl.complete_date,\r\n       psl.log_type,\r\n       psl.is_sync,\r\n       r.deptcode,\r\n       r.site_id,\r\n       r.service_id,\r\n        r.deptname,\r\n        zong.zongclasshour,\r\n        DECODE(exam.SCORE,\r\n               null,\r\n               \'-\',\r\n               DECODE(SIGN(exam.SCORE - exam.PERCENT), -1, \'不达标\', \'达标\')) HEGE,\r\n               (r.COMPULSORY_STANDARD + r.OPTIONAL_STANDARD) dabiao        \r\n     from report_userinfo r\r\n left JOIN  report_zongclasshour zong\r\n     ON zong.PROJECT_ID = r.PROJECT_ID\r\n left JOIN (select PROJECT_ID, LOGIN_NAME,SITE_PROJECT_ID,max(score)score,max(percent)percent\r\n               from report_exam \r\n              group by PROJECT_ID, LOGIN_NAME, SITE_PROJECT_ID)exam\r\n    ON exam.PROJECT_ID = r.PROJECT_ID\r\n   AND exam.LOGIN_NAME = r.ACCOUNT_NAME\r\n   and exam.site_project_id = r.SITE_PROJECT_ID\r\n INNER JOIN report_course_project ppc\r\n    ON r.PROJECT_ID = ppc.PROJECT_ID --项目课程\r\n INNER JOIN PXPT_COURSE_CWARE pcc\r\n    ON ppc.COURSE_ID = pcc.COURSE_ID --课件\r\n  LEFT JOIN PXPT_STUDY_LOG psl\r\n    ON psl.LOGIN_NAME = r.ACCOUNT_NAME\r\n   AND psl.SITE_PROJECT_ID = r.SITE_PROJECT_ID\r\n   and psl.CWARE_ID = pcc.CWARE_ID\r\n   where r.site_id in (\'1082\');', '1', '', '81', '5', '7', '89', '51', '9');
