INSERT INTO `daily_report_dba` VALUES ('224', '2018-02-12', '按照逗号分割数据为多行', 'with t as\r\n (select i.org_id a\r\n    from jypx_cms_info i\r\n   where i.info_id = \'b293a30008d340e98da4dca4573ae7c1\')\r\nselect substr(a,\r\n              decode(level - 1, 0, 0, instr(a, \',\', 1, level - 1)) + 1,\r\n              (decode(level,\r\n                      regexp_count(a, \',\') + 1,\r\n                      length(a) + 1,\r\n                      instr(a, \',\', 1, level))) -\r\n              (decode(level - 1, 0, 0, instr(a, \',\', 1, level - 1)) + 1)) org_id\r\n  from t\r\nconnect by level <= regexp_count(a, \',\') + 1;', '1', '', '4', '90', '7', '214', '207', '9');
