INSERT INTO `daily_report_dba` VALUES ('229', '2018-02-22', '河北省级一类项目授权', 'select * from   imp_tmp_tab t  for update \r\nupdate  imp_tmp_tab set col1=trim(col1),col2=trim(col2),col3=trim(col3)\r\n\r\n\r\nselect * from imp_tmp_tab\r\n\r\n\r\n\r\n                                                                                                   \r\n           --cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab;                                                                                  \r\n                                                                                                                             \r\n           delete  imp_tmp_tab ;                                                                                       \r\n                       select* from ncme_course_credit c  where c.course_id=\'201600921361\'\r\n                        and c.course_serial=\'2018-01-01-001 (冀)\'                                                                                                       \r\n           insert into ncme_course_credit                                                                                    \r\n           select t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           t.col3,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n         where c.course_id=\'201600921361\'\r\n                        and c.course_serial=\'2018-01-01-001 (冀)\' \r\n           \r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )', '1', '', '4', '90', '2', '97', '8', '58');
INSERT INTO `daily_report_dba` VALUES ('230', '2018-02-23', '威海生成外部id', 'select * from  imp_tmp_tab for update ;\r\nupdate imp_tmp_tab set col1=trim(col1);\r\n\r\n201800006894\r\nselect ncme_district_course_seq.nextval+118 from dual;\r\n\r\nupdate imp_tmp_tab set col2=201800006893+rownum\r\n\r\n\r\nselect * from ncme_district_course  dd where dd.remark like \'%威海%\'\r\nand dd.district_id=\'27008\'\r\n\r\ninsert into ncme_district_course\r\nselect col2,col1, \'27008\',\'2018\',\'威海2018年合作项目\',sysdate from imp_tmp_tab', '1', '', '4', '90', '2', '97', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('231', '2018-02-23', '对应北京乡医机构id', 'select * from icme_student_rank_tran_log l where ic_code in(\r\nselect ic_code from icme_student s where s.org_id in(\r\nselect o.org_id from icme_org o where o.org_code like \'000200230056%\'))\r\n\r\n\r\nselect * from sip_auth_org_info io where id in(\r\nselect org_id from sip_auth_org_tree where map_id=1315\r\n)\r\n\r\nand io.org_fixed_type_id=5\r\nand org_name in(\'桥梓镇社区卫生服务中心\',\r\n\'四季青医院\',\r\n\'于家务卫生院\')\r\nand status=1\r\n\r\nselect * from co_site s where s.site_host=\'bjxy.haoyisheng.com\'\r\nselect * from sip_auth_org_map where site_id=1282\r\n\r\nselect * from icme_org o\r\njoin icme_org_type ot on ot.org_type_id=o.org_type_id and ot.org_id=37000000\r\n where o.org_code like \'000200230056%\'\r\nand ot.org_levle=3\r\nselect * from icme_year y where y.icme_year_id=100002022\r\n\r\nselect * from icme_org o where o.parent_org_id=100394531\r\nselect * from   imp_tmp_tab for update \r\n\r\n\r\nselect * from \r\n\r\n\r\n\r\nmerge into icme_org oo\r\nusing (select distinct o.org_id,t.col1 from icme_org o join\r\nimp_tmp_tab  t on t.col2=o.org_name \r\njoin icme_org_type ot on ot.org_type_id=o.org_type_id and ot.org_id=37000000\r\n where o.org_code like \'000200230056%\'\r\nand ot.org_levle=3 ) aa on (aa.org_id=oo.org_id)\r\nwhen matched then update set oo.country_org_id=aa.col1\r\n\r\n\r\n\r\n\r\nselect * from imp_tmp_tab where col2 in(\r\n\'桥梓镇社区卫生服务中心\',\r\n\'四季青医院\',\r\n\'于家务卫生院\'\r\n) for update  \r\n\r\n200259924		桥梓镇社区卫生服务中心\r\n200217844		四季青医院\r\n200218020		于家务卫生院\r\n\r\n\r\n\r\n\r\nselect * from icme_org o\r\njoin icme_org_type ot on ot.org_type_id=o.org_type_id and ot.org_id=37000000\r\n where o.org_code like \'000200230056%\'\r\nand ot.org_levle=3 and org_name in(\'桥梓镇社区卫生服务中心\',\r\n\'四季青医院\',\r\n\'于家务卫生院\')\r\n\r\n\r\n\r\nselect  o.org_id,o.org_name from icme_org o join\r\nimp_tmp_tab  t on t.col2=o.org_name \r\njoin icme_org_type ot on ot.org_type_id=o.org_type_id and ot.org_id=37000000\r\n where o.org_code like \'000200230056%\'\r\nand ot.org_levle=3  group by o.org_id,o.org_name\r\n having count(1) >1', '1', '', '6', '115', '2', '97', '8', '54');
