INSERT INTO `daily_report_dba` VALUES ('234', '2018-02-24', '安徽授权项目修改项目编号', 'select * from   imp_tmp_tab t  for update \r\nupdate  imp_tmp_tab set col1=trim(col1),col2=trim(col2),col3=trim(col3)\r\n\r\n\r\nselect * from imp_tmp_tab\r\n\r\n\r\n\r\n                                                                                                   \r\n           --cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab;                                                                                  \r\n                                                                                                                             \r\n           delete  imp_tmp_tab ;   \r\n           \r\n           select * from            ncme_course_credit where organ_id in(                                                                         \r\n                       select organ_id from ncme_course_credit c  where c.course_id=\'201600919338\'\r\n                        and c.course_serial=\'AH-2018-001\' )\r\n                        and     course_id=\'201400010989\'   \r\n                                                                                                                       \r\n           insert into ncme_course_credit                                                                                    \r\n           select t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           t.col3,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n         where c.course_id=\'201600921361\'\r\n                        and c.course_serial=\'2018-01-01-001 (冀)\' \r\n           \r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )\r\n           \r\n           \r\n           merge into ncme_course_credit aa using(\r\n   select tt.course_id, tt.organ_id, t.col2\r\n     from ncme_course_credit tt\r\n     join imp_tmp_tab t\r\n       on t.col1 = tt.course_id\r\n    where organ_id in (select organ_id\r\n                         from ncme_course_credit c\r\n                        where c.course_id = \'201600919338\'\r\n                          and c.course_serial = \'AH-2018-001\')\r\n                          and tt.credit_year=\'2018\') bb \r\n                          on(aa.course_id=bb.course_id\r\n                          and aa.organ_id=bb.organ_id\r\n                          )\r\n                          when matched then \r\n                            update set  aa.course_serial=\r\n                            bb.col2\r\n                            where aa.credit_year=\'2018\'\r\n                       \r\n                    \r\nupdate  imp_tmp_tab   set col2=trim(col2)     \r\n\r\n\r\n\r\n\r\nselect * from ncme_study_credit cs where (cs.course_id,cs.organ_id)  in(\r\n\r\n   select tt.course_id, tt.organ_id\r\n     from ncme_course_credit tt\r\n     join imp_tmp_tab t\r\n       on t.col1 = tt.course_id\r\n    where organ_id in (select organ_id\r\n                         from ncme_course_credit c\r\n                        where c.course_id = \'201600919338\'\r\n                          and c.course_serial = \'AH-2018-001\')\r\n                          and tt.credit_year=\'2018\'\r\n)\r\nand cs.serial_number is not null\r\n      \r\n merge into ncme_study_credit aa using(\r\n   select tt.course_id, tt.organ_id, t.col2\r\n     from ncme_course_credit tt\r\n     join imp_tmp_tab t\r\n       on t.col1 = tt.course_id\r\n    where organ_id in (select organ_id\r\n                         from ncme_course_credit c\r\n                        where c.course_id = \'201600919338\'\r\n                          and c.course_serial = \'AH-2018-001\')\r\n                          and tt.credit_year=\'2018\') bb \r\n                          on(aa.course_id=bb.course_id\r\n                          and aa.organ_id=bb.organ_id\r\n                          )\r\n                          when matched then \r\n                            update set  aa.serial_number=\r\n                            bb.col2\r\n                            where aa.apply_year=\'2018\'', '1', '', '4', '90', '2', '97', '8', '58');
INSERT INTO `daily_report_dba` VALUES ('235', '2018-02-24', 'cme课件库修改学科', 'merge into kjk_courseware kcc using (\r\nselect nc.course_id || nc.courseware_id cc_id,ns.subject2_name, ns.subject_name\r\n  from ncme_courseware nc\r\n  join ncme_course ncc\r\n    on ncc.course_id = nc.course_id\r\n  join ncme_subject ns\r\n    on ns.subject_id = ncc.subject_id\r\n where nc.course_id || nc.courseware_id in\r\n       (select code\r\n          from kjk_courseware kc\r\n         where kc.subject is null\r\n            or kc.subject2 is null)) tt\r\n            \r\n            on (kcc.code=tt.cc_id)\r\n            when matched then update set kcc.subject=tt.subject_name,\r\n              kcc.subject2=tt.subject2_name', '1', '', '4', '90', '2', '97', '207', '9');
INSERT INTO `daily_report_dba` VALUES ('236', '2018-02-24', '授权邢台邯郸数据', 'insert into ncme_course_credit                                                                                    \r\n           select t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           c.course_serial,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n