INSERT INTO `daily_report_dba` VALUES ('249', '2018-02-26', '上午数据调取', 'select\r\n        \r\n      user_getdistrict(re.user_x_id, 1) 省,\r\n         user_getdistrict(re.user_x_id, 2) 市,\r\n         user_getdistrict(re.user_x_id, 3) 县,\r\n                User_getUserInfo(re.login_name, 1) 科室,\r\n                \r\n         User_getUserInfo(re.login_name, 2) 职称,\r\n         \r\n         user_getuserinfo(re.login_name, 4) 医院级别,count(1) \r\n        \r\n          from user_register re\r\n         where substr(re.user_x_id, 1, 2) not in (\'10\', \'19\', \'32\')\r\n           and re.dept_id in\r\n               (\'K1000006\', \'K8000009\', \'K3000002\', \'K8000012\')\r\n               \r\n               group by \r\n                   user_getdistrict(re.user_x_id, 1) ,\r\n         user_getdistrict(re.user_x_id, 2) ,\r\n         user_getdistrict(re.user_x_id, 3) ,\r\n                User_getUserInfo(re.login_name, 1) ,\r\n                \r\n         User_getUserInfo(re.login_name, 2) ,\r\n         \r\n         user_getuserinfo(re.login_name, 4);\r\n         \r\n         \r\n\r\nselect\r\n        \r\n       User_getUserInfo(re.login_name, 2) 职称,count(1) \r\n        \r\n          from user_register re\r\n         where substr(re.user_x_id, 1, 2) not in (\'10\', \'19\', \'32\')\r\n           and re.dept_id in\r\n               (\'K1009999\')\r\n               \r\n               group by \r\n                  User_getUserInfo(re.login_name, 2) ;\r\n                   \r\n      \r\n      \r\n      \r\nselect\r\n        \r\n      user_getdistrict(re.user_x_id, 1) 省,count(1) \r\n        \r\n          from user_register re\r\n         where substr(re.user_x_id, 1, 2) not in (\'10\', \'19\', \'32\')\r\n           and re.dept_id in\r\n               (\'K1009999\')\r\n               \r\n               group by \r\n                   user_getdistrict(re.user_x_id, 1) ;\r\n                            \r\n               \r\n               \r\n               \r\n               \r\n     