INSERT INTO `daily_report_dba` VALUES ('252', '2018-02-27', '执行脚本', 'select * from imp_tmp_tab                                                                                       \r\n           --cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab where col1=\'201500011943\'                                                                                  \r\n                            \r\n           \r\n           update imp_tmp_tab set col1=trim(col1),col2=trim(col2)\r\n                                                                                                            \r\n           delete imp_tmp_tab;\r\n           select *\r\n             from ncme_course_credit c\r\n            where c.course_id = \'201600920314\'\r\n              and c.organ_id in(\'000000000039\',\'000000000024\',\'000000000043\',\'000000000040\')\r\n\r\n\r\n\r\n\r\n  insert into ncme_course_credit                                                                                    \r\n           select t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           c.course_serial,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n        where c.course_id = \'201600920314\'\r\n              and c.organ_id in(\'000000000039\',\'000000000024\',\'000000000043\',\'000000000040\')\r\n\r\n           \r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )  \r\n           \r\ndelete from  ncme_course_credit cs  where cs.credit_date>sysdate-1/48;\r\n\r\n\r\n select * from imp_tmp_tab                                                                                       \r\n           --cme课程授权                                                                                                     \r\nselect col1 from  imp_tmp_tab  group by\r\ncol1 having count(1) >1                                                                  \r\n                            \r\n           \r\n           update imp_tmp_tab set col1=trim(col1),col2=trim(col2)\r\n                                                                                                            \r\n           delete imp_tmp_tab;\r\n           select *\r\n             from ncme_course_credit c\r\n            where c.course_id = \'201600919178\'\r\n              and c.organ_id in(\'000000000039\',\'000000000024\',\'000000000043\',\'000000000040\')\r\n\r\n\r\n\r\n\r\n  insert into ncme_course_credit                                                                                    \r\n           select distinct t.col1,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           c.course_serial,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n        where c.course_id = \'201600919178\'\r\n              and c.organ_id in(\'000000000039\',\'000000000024\',\'000000000043\',\'000000000040\')\r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )', '1', '', '4', '90', '2', '97', '8', '58');
