INSERT INTO `daily_report_dba` VALUES ('257', '2018-02-27', '数据调取', 'select  h.card_type,pc.card_name,user_getdistrict(re.d_id, 4) ,count(1) ,\'聊城\'\r\n  from ncme_study_card h\r\njoin ncme_pay_card pc on pc.card_type=h.card_type join user_register re on re.login_name=\r\nh.user_name where\r\n re.user_x_id like \'27015%\'\r\n   \r\nand pc.card_name in(\r\n\r\n\'2017CME山东聊城10分赠卡\',\r\n\'2017CME山东3分赠卡\',\r\n\'2017CME山东学习卡3\',\r\n\'2017CME山东学习卡\',\'2017CME山东学习卡\'\r\n) group by h.card_type,pc.card_name,user_getdistrict(re.d_id, 4) union all\r\n\r\n\r\nselect  h.card_type,pc.card_name,user_getdistrict(re.d_id, 4) ,count(1) ,\'聊城\'\r\n  from ncme_study_card)_history h\r\njoin ncme_pay_card pc on pc.card_type=h.card_type join user_register re on re.login_name=\r\nh.user_name where\r\n re.user_x_id like \'27015%\'\r\n   \r\nand pc.card_name in(\r\n\r\n\'2017CME山东聊城10分赠卡\',\r\n\'2017CME山东3分赠卡\',\r\n\'2017CME山东学习卡3\',\r\n\'2017CME山东学习卡\',\'2017CME山东学习卡\'\r\n) group by h.card_type,pc.card_name,user_getdistrict(re.d_id, 4)', '1', '', '4', '90', '2', '91', '8', '57');
