INSERT INTO `daily_report_dba` VALUES ('262', '2018-02-28', '山东聊城帮卡数据统计', '永莲，能帮忙查一下聊城赠卡去年的使用人和今年为同一人的几率是多少吗？\r\n\r\n另外再帮忙查一下近三年聊城使用非赠卡的绑卡数，谢谢。只要数即可\r\n\r\n\r\nselect * from user_district d where d.district_name like \'%聊城%\'\r\n17年赠卡使用人数：1187  之前有绑卡记录的人数：1103\r\n赠卡绑定人数：2846 \r\n非赠卡绑定人数：3263\r\n2017	1841\r\n2016	1563\r\n2015	1690\r\n\r\n\r\nselect y.year_name,count(1)  from ncme_study_card_history h join user_register re on re.login_name=h.user_name\r\njoin ncme_pay_card pc on pc.card_type=h.card_type join cme_year y on h.link_date between y.year_begin_date and y.year_end_date\r\nwhere re.user_x_id like \'27015%\' and pc.price<>0\r\nand pc.start_date>=to_date(\'2015-2-1\',\'yyyy-MM-dd\')\r\ngroup by y.year_name\r\n\r\n\r\nselect h.* \r\n  from ncme_study_card_history h\r\n  join user_register re\r\n    on re.login_name = h.user_name\r\n  join ncme_pay_card pc\r\n    on pc.card_type = h.card_type\r\n where re.user_x_id like \'27015%\'\r\n   and pc.price <> 0\r\n   and pc.start_date >= to_date(\'2015-2-1\', \'yyyy-MM-dd\')\r\n   and link_date is null\r\n group by substr(h.card_type, 0, 4);\r\n \r\nselect  count(1)\r\n  from ncme_study_card h\r\n  join user_register re\r\n    on re.login_name = h.user_name\r\n  join ncme_pay_card pc\r\n    on pc.card_type = h.card_type\r\n where re.user_x_id like \'27015%\'\r\n   and pc.price <> 0\r\n   and pc.start_date >= to_date(\'2015-2-1\', \'yyyy-MM-dd\')\r\n   and link_date is null\r\n\r\n\r\n\r\n\r\nselect user_name from ncme_study_card_history where user_name in(\r\nselect re.login_name  from ncme_study_card_history h join user_register re on re.login_name=h.user_name\r\njoin ncme_pay_card pc on pc.card_type=h.card_type\r\nwhere re.user_x_id like \'27015%\' and pc.price=0\r\nand pc.start_date>=to_date(\'2017-2-1\',\'yyyy-MM-dd\')\r\nand h.card_type in(\'2017sd3fz\',\'2017sd10fz\'))\r\ngroup by user_name having count(1) >=2\r\n\r\n2017sd10fz  2017sd3fz\r\n\r\nselect * from ncme_pay_card pc  where pc.price=0 and pc.card_type like \'%sd%\'\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nselect distinct pc.*  from ncme_study_card_history h join user_register re on re.login_name=h.user_name\r\njoin ncme_pay_card pc on pc.card_type=h.card_type\r\nwhere re.user_x_id like \'27015%\' and pc.price=0\r\nand pc.start_date>=to_date(\'2015-2-1\',\'yyyy-MM-dd\')；', '1', '', '4', '90', '2', '89', '8', '57');
