INSERT INTO `daily_report_dba` VALUES ('279', '2018-03-01', '修改课程课件id', 'select replace(t.course_id,\'F\',\'2\')||t.courseware_id as code,t.product_id from ncme_courseware t \r\nwhere t.product_id is null and t.type in (4,5)\r\nand t.course_id  like \'%F%\'\r\n\r\nupdate    kjk_courseware  set status=1 where code IN(\r\nselect code from kjk_courseware t1 where t1.play_type=1\r\nAND T1.STATUS=0\r\ngroup by code having count(1) >1)\r\nand rowid\r\n not in(select max(rowid) from kjk_courseware t1 where t1.play_type=1\r\nAND T1.STATUS=0\r\ngroup by code having count(1) >1)\r\n\r\n\r\n)\r\n\r\n\r\nmerge into ncme_courseware t \r\nusing (select distinct t1.code,t1.id as product_id from kjk_courseware t1 where t1.play_type=1\r\nand code is not null\r\nand status=0 )\r\na \r\non (a.code=t.course_id||t.courseware_id )\r\nwhen matched then update set t.product_id=a.product_id\r\nwhere t.product_id is null and t.type in (4,5)\r\nand t.course_id not like \'%F%\'\r\n\r\n\r\nmerge into ncme_courseware t \r\nusing (select distinct t1.code,t1.id as product_id from kjk_courseware t1 where t1.play_type=1\r\nand code is not null\r\nand status=0 )\r\na \r\non (a.code=replace(t.course_id,\'F\',\'2\')||t.courseware_id )\r\nwhen matched then update set t.product_id=a.product_id\r\nwhere t.product_id is null and t.type in (4,5)\r\nand t.course_id  like \'%F%\'', '1', '', '4', '90', '2', '97', '32', '9');
