INSERT INTO `daily_report_dba` VALUES ('290', '2018-03-01', '将nproject_project  的 backorgid  字段修改为 creator_id\r\n的上级中级别最低且backauthority = 1 的机构id', 'declare\r\n  v_backorgid varchar2(200);\r\n/*  找到所有子孙创建的项目id */ \r\n CURSOR R1 IS\r\n    select id, creator_id\r\n      from nproject_project\r\n     where year = 2017\r\n       and creator_id in\r\n           (select o.sysuser_id\r\n              from nproject_organization o\r\n             start with o.sysuser_id = 99\r\n            connect by prior o.sysuser_id = o.parent_id);\r\nbegin\r\n  for i1 in r1 loop\r\n   /* 找到创建机构的上级中backauthority = 1且级别最低的*/\r\n    select sysuser_id\r\n      into v_backorgid\r\n      from (select o.sysuser_id,\r\n                   CONNECT_BY_ROOT sysuser_id root_id,\r\n                   o.backauthority,\r\n                   level           lv\r\n              from nproject_organization o\r\n             where backauthority = 1\r\n             start with o.sysuser_id = i1.creator_id\r\n            connect by prior o.parent_id = o.sysuser_id\r\n             order by lv )\r\n     where rownum = 1;\r\n    update nproject_project set backorgid = v_backorgid where id = i1.id;\r\n    commit;\r\n  end loop;\r\nend;', '1', '', '66', '117', '7', '214', '36', '9');
INSERT INTO `daily_report_dba` VALUES ('291', '2018-03-02', '优化sql', 'select distinct t.*,\r\n                t1.sex,\r\n                t1.highest_degree,\r\n                t1.first_degree,\r\n                t1.job_number,\r\n                t2.id as zyybaseresidency,\r\n                t1.user_post_title,\r\n                account.account_password,\r\n                account.account_id,\r\n                t1.has_certificate,\r\n                t1.certificate_number,\r\n                t1.mobil_number,\r\n                t1.highest_record_school,\r\n                t1.certificate_no,\r\n                t1.certificate_type,\r\n                o1.org_name,\r\n                cityorg.id cityorgid,\r\n                cityorg.org_name cityorgname,\r\n                countyorg.id countyorgid,\r\n                countyorg.org_name countyorgname,\r\n                zcb.id as cultivatebatchid,\r\n                zcb.cultivate_batch_name,\r\n                (case\r\n                  when minors is null then\r\n                   0\r\n                  else\r\n                   minors\r\n                end) minors,\r\n                zuc.id as usercultivatebatchid,\r\n                t1.physicians_practicing_cert,\r\n                t1.scope_certificate,\r\n                t1.category_certificate,\r\n                t1.get_date,\r\n                t1.practice_certificate_number,\r\n                t2.base_id as baseid,\r\n                t6.id base_id,\r\n                t7.name basename,\r\n                t7.alias_name basealiasname,\r\n                t5.school_system,\r\n                t5.stage,\r\n                t5.is_exam_passed,\r\n                t5.graduation_year,\r\n                t5.status extendstatus,\r\n                t5.has_certificate hascert,\r\n                t4.dept_name,\r\n                t3.start_date,\r\n                t3.end_date\r\n  from (select *\r\n          from zyy_user t\r\n         where exists (select 1\r\n                  from zyy_org\r\n                 where id = t.zyy_user_org_id\r\n                   and admin_parent_org_id = 70488)) t\r\n  join base_account account\r\n    on account.account_name = t.account_name\r\n  join zyy_user_extend t1\r\n    on t.id = t1.zyy_user_id\r\n  join zyy_org o1\r\n    on t.zyy_user_org_id = o1.id\r\n   and o1.status > 0\r\n  left join zyy_org cityorg\r\n    on cityorg.id = o1.admin_parent_org_id\r\n  left join zyy_org countyorg\r\n    on countyorg.id = o1.country_parent_org_id\r\n  join zyy_base_residency t2\r\n    on t2.residency_id = t.id\r\n  join zyy_base t6\r\n    on t6.id = t2.base_id\r\n   and t6.hospital_id = t.zyy_user_org_id\r\n  join zyy_base_std t7\r\n    on t7.id = t6.base_std_id\r\n  left join zyy_user_cultivatebatch zuc\r\n    on zuc.userid = t.id\r\n  left join zyy_cultivate_batch zcb\r\n    on zcb.id = zuc.cultivatebatchid\r\n  left join zyy_recruit_residency_will t8\r\n    on t6.hospital_id = t8.hospital_id\r\n   and t8.base_id = t2.base_id\r\n   and t8.residency_id = t.id\r\n  left join zyy_recruit_stage t9\r\n    on t8.recruit_stage_id = t9.id\r\n   and t9.year_id = t2.recruit_year_id\r\n  left join zyy_cycle_table_resi_cycle t3\r\n    on t3.residency_id = t.id\r\n   and t3.start_date <= trunc(sysdate)\r\n   and t3.end_date >= trunc(sysdate)\r\n   and t3.status = 1\r\n  left join zyy_dept t4\r\n    on t4.id = t3.dept_id\r\n   and t.zyy_user_org_id = t4.hospital_id\r\n  left join zyy_residency_user_extend t5\r\n    on t5.zyy_user_id = t.id\r\n   and t5.status = 1\r\n where (exists\r\n        (select \'x\'\r\n           from zyy_recruit_residency_will x\r\n          where x.residency_id = t8.residency_id\r\n            and x.hospital_id = t8.hospital_id\r\n            and (x.status = 41 or x.status is null)) or\r\n        (t8.status = 41 or t8.status is null) or t2.residency_source = 4)\r\n   and t.zyy_user_status = 1\r\n order by t.real_name asc', '1', '', '78', '203', '7', '235', '39', '9');
