INSERT INTO `daily_report_dba` VALUES ('295', '2018-03-02', '调取数据', '--导出cme数据\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,\r\n      --  re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n      case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n    -- re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       User_getUserInfo(re.login_name, 3) 学历,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n    sc.card_no 付费卡号,\r\n      nc.name 课程名称,\r\n sc.apply_subtype 申请子类型,\r\nsc.startdate 开始日期,\r\n sc.passdate 通过日期,\r\n --sc.applydate 申请日期,\r\n      sc.senddate 发证日期\r\n     -- sc.apply_credit  学分,\r\n     -- sc.apply_credit*3 学时,\r\n      --ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where sc.course_id in (\'201700921019\'\r\n                       )\r\n and sc.startdate>=to_date(\'2018-2-1\',\'yyyy-mm-dd\')\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion  \r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,\r\n     --   re.cme_cert_num 身份证号,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n       null 付费卡号,\r\n       nc.name 课程名称,\r\n       null 申请子类型,\r\n       sc.startdate 开始日期,\r\n       sc.passdate 通过日期,\r\n       --null 申请日期,\r\n       null 发证日期\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where sc.course_id in (\'201700921019\'\r\n                       )\r\n and sc.startdate>=to_date(\'2018-2-1\',\'yyyy-mm-dd\')', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('296', '2018-03-02', '修改数据', 'select * from zyy_user_cultivatebatch t where t.userid in (458499,458370)for update ;\r\n\r\nselect *\r\n  from zyy_user_cultivatebatch\r\n where (cultivatebatchid, userid) in\r\n       (select t.cultivatebatchid, t.userid\r\n          from zyy_user_cultivatebatch t\r\n         group by t.cultivatebatchid, t.userid\r\n        having count(1) >= 2)\r\n\r\n\r\nand cultivatebatchid=1261 for update \r\n\r\n\r\n\r\n\r\nupdate    zyy_user_cultivatebatch s  set s.cultivatebatchid=1101\r\n where (cultivatebatchid, userid) in\r\n       (select t.cultivatebatchid, t.userid\r\n          from zyy_user_cultivatebatch t\r\n         group by t.cultivatebatchid, t.userid\r\n        having count(1) >= 2)\r\n        and rowid not in(\r\n        select max(rowid)\r\n          from zyy_user_cultivatebatch t\r\n         group by t.cultivatebatchid, t.userid\r\n        having count(1) >= 2\r\n        )\r\n\r\n\r\nand cultivatebatchid=1261', '1', '', '78', '203', '2', '97', '39', '9');
INSERT INTO `daily_report_dba` VALUES ('297', '2018-03-02', '课程授权', '--cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab;                                                                                  \r\n                                                                                                                             \r\n           delete  imp_tmp_tab ;                                                                                       \r\n                       select* from ncme_course_credit c  where c.course_id=\'F01600920626\'\r\n                        and c.organ_id=\'000000000081\'                           \r\n                        附件中的课程就是要授权给江苏省卫生厅（000000000011 ）、\r\n                        江苏省常州市卫生局（000000000100  ）\r\n                        、无锡市卫生局（000000000152  ）  这三个地区的吗？                                                                          \r\n           insert into ncme_course_credit                                                                                    \r\n           select t.col1,                                                                                                    \r\n           \'000000000152\',                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           c.credit_num,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           c.course_serial,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n           where c.course_id=\'F01600920626\'\r\n                        and c.organ_id=\'000000000081\'     \r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col1\r\n           and css.organ_id=\'000000000152\' and css.credit_year=\'2018\' );', '1', '', '4', '90', '2', '97', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('298', '2018-03-02', '课程授权', 'insert into ncme_course_credit                                                                                    \r\n           select t.col3,                                                                                                    \r\n           organ_id,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col1,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           col2,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n               