INSERT INTO `daily_report_dba` VALUES ('303', '2018-03-05', '数据统计', 'select apply_year,count(1) \r\n  from ncme_study_credit_history h\r\n where h.organ_id in (select o.organ_id\r\n                        from ncme_admin_organ o\r\n                       where o.district_id like \'37%\')\r\n                       and h.apply_year in(\'2016\',\'2017\')\r\n                       and h.credit_type=\'G1\'\r\n                       group by apply_year\r\n                       \r\n                \r\nselect count(1) \r\n  from ncme_study_credit_history h\r\n  join user_register re on re.login_name=h.user_name\r\n  \r\n where re.user_x_id like \'37%\'\r\n and h.startdate>=to_date(\'2016-7-1\',\'yyyy-MM-dd\')\r\n      and h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\')              \r\n                AND COURSE_ID IN(\r\n   select COURSE_ID from Ncme_Course_Credit_History cs where cs.credit_type=\'G1\'\r\nAND  organ_id in (select o.organ_id\r\n                        from ncme_admin_organ o\r\n                       where o.district_id like \'37%\')       \r\n                       AND CS.CREDIT_YEAR=\'2017\'    \r\n                               )', '1', '', '4', '90', '2', '91', '8', '56');
