INSERT INTO `daily_report_dba` VALUES ('314', '2018-03-06', '烦请查询甘肃省的2017年的国家级项目中学科授权带有“中医/中西医”字眼的调取出来，谢谢！\r\n字段为：课题ID、课题名称、专家、专家单位、二三级学科', 'select * from  ncme_course nc where nc.name like \'%消化系统疑难%\'\r\n\r\n\r\n201700921019\r\n\r\nselect * from ncme_admin_organ o where o.name like \'%甘肃%\'\r\n\r\nselect H.COURSE_ID\r\n  from ncme_course_credit_history h\r\n where  organ_id = \'000000000149\'\r\n and h.credit_year=\'2017\'\r\n and h.credit_type=\'G1\'\r\n   课题ID、课题名称、专家、专家单位、二三级学科\r\n   \r\n   \r\n   select cs.course_id,nc.name,nc.* from ncme_course_subject cs\r\n   join ncme_course nc on nc.course_id=cs.course_id\r\n   join ncme_subject su on su.subject_id=cs.subject_id where cs.course_id IN(\r\n   \r\nselect H.COURSE_ID\r\n  from ncme_course_credit_history h\r\n where  organ_id = \'000000000149\'\r\n and h.credit_year=\'2017\'\r\n and h.credit_type=\'G1\'\r\n   ) AND CS.SUBJECT_ID IN(\r\n\r\n\r\nselect subject_id from ncme_subject s where s.subject_name like \'%中%\')', '1', '', '4', '90', '2', '91', '8', '56');
