INSERT INTO `daily_report_dba` VALUES ('316', '2018-03-07', '执行脚本', '-- icme预约系统修改，需要正式环境执行的sql\r\n\r\n-- 1 增加 公布不公布字段\r\n-- Add/modify columns \r\nalter table icme_org add publish_status INTEGER;\r\n-- Add comments to the columns \r\ncomment on column icme_org.publish_status\r\n  is \'公布权限状态 1：有公布权限 2：无公布权限\';\r\n  \r\n-- 2 增加 经纬度字段 \r\n-- Add/modify columns \r\nalter table ICME_SUBJECT_CARD_TIME add LOCATION_LNG NUMBER(10,7);\r\n-- Add comments to the columns \r\ncomment on column ICME_SUBJECT_CARD_TIME.LOCATION_LNG\r\nis \'经度\';\r\n\r\n-- Add/modify columns \r\nalter table ICME_SUBJECT_CARD_TIME add LOCATION_LAT NUMBER(10,7);\r\n-- Add comments to the columns \r\ncomment on column ICME_SUBJECT_CARD_TIME.LOCATION_LAT\r\nis \'纬度\';\r\n\r\n-- Add/modify columns \r\nalter table ICME_SUBJECT_CARD_TIME add SIGN_RANGE INTEGER;\r\n-- Add comments to the columns \r\ncomment on column ICME_SUBJECT_CARD_TIME.SIGN_RANGE\r\nis \'签到范围\';\r\n\r\n-- 3 增加  省市县字段\r\n-- Add/modify columns \r\nalter table ICME_ORG add province_id VARCHAR2(9);\r\n-- Add comments to the columns \r\ncomment on column ICME_ORG.province_id\r\nis \'省\';\r\n\r\n-- Add/modify columns \r\nalter table ICME_ORG add city_id VARCHAR2(9);\r\n-- Add comments to the columns \r\ncomment on column ICME_ORG.city_id\r\nis \'市\';\r\n\r\n-- Add/modify columns \r\nalter table ICME_ORG add county_id VARCHAR2(9);\r\n-- Add comments to the columns \r\ncomment on column ICME_ORG.county_id\r\nis \'县\';\r\n\r\n-- 4 增加 预约单位类型 字段\r\n-- Add/modify columns \r\nalter table ICME_PROJECT_APPOINTMENT add appointment_type INTEGER;\r\n-- Add comments to the columns \r\ncomment on column ICME_PROJECT_APPOINTMENT.appointment_type\r\nis \'预约单位类型\';\r\n\r\n\r\n-- 5 创建新表：项目预约机构表   \r\ncreate table ICME_PROJECT_APPOINTMENT_ORG \r\n(\r\n   ID                   INTEGER              not null,\r\n   PROJECT_ID           INTEGER,\r\n   ORG_ID               INTEGER,\r\n   constraint PK_ICME_PROJECT_APPOINTMENT_OR primary key (ID)\r\n);\r\n\r\ncomment on table ICME_PROJECT_APPOINTMENT_ORG is\r\n\'教学项目课题管理  --【编辑项目】进入 添加可预约单位的选择\';\r\n\r\n-- Create sequence \r\ncreate sequence ICME_PROJECT_APTMENT_ORG_SEQ               \r\nminvalue 1\r\nmaxvalue 999999999\r\nstart with 1\r\nincrement by 1\r\ncache 20;\r\n\r\n-- 6  创建新表： APP二维码生效开始时间表\r\ncreate table ICME_PHONE_SET_APP \r\n(\r\n   ID                   INTEGER              not null,\r\n   SUBJECT_ID           INTEGER,\r\n   ADMIN_ID             NUMBER,\r\n   CREATE_TIME          DATE,\r\n   UPDATE_TIME          DATE,\r\n   constraint PK_ICME_PHONE_SET_APP primary key (ID)\r\n);\r\n\r\ncomment on table ICME_PHONE_SET_APP is\r\n\'APP二维码生效开始时间表,和ICME_PHONE_SET_ADMIN表一样，但是数据不同\';\r\n\r\ncomment on column ICME_PHONE_SET_APP.SUBJECT_ID is\r\n\'icme_subject(subject_id)\';\r\n\r\ncomment on column ICME_PHONE_SET_APP.ADMIN_ID is\r\n\'icme_admin(admin_id)\';    \r\n\r\n-- Create sequence \r\ncreate sequence ICME_PHONE_SET_APP_SEQ               \r\nminvalue 1\r\nmaxvalue 999999999\r\nstart with 1\r\nincrement by 1\r\ncache 20;\r\n\r\n\r\nALTER TABLE ICME_PHONE_BASE_SET ADD(WX_ID VARCHAR2(100) DEFAULT \' \' NOT NULL);\r\ncomment on column ICME_PHONE_BASE_SET.WX_ID   is  \'此账号对应的微信Id\';  \r\nALTER TABLE ICME_PHONE_BASE_SET ADD(TOKEN VARCHAR2(100) default \'sign\');\r\ncomment on column ICME_PHONE_BASE_SET.TOKEN   is  \'对应的token\';  \r\n\r\n\r\nALTER TABLE ICME_PHONE_ADMIN_BIND ADD ORG_ID NUMBER  default 1;\r\ncomment on column ICME_PHONE_ADMIN_BIND.ORG_ID   is  \'此账号对应的baseSet表中的orgId\';  \r\nALTER TABLE ICME_PHONE_ADMIN_BIND ADD(ADMIN_ID NUMBER );\r\nALTER TABLE ICME_PHONE_ADMIN_BIND ADD(ORG_NAME VARCHAR2(128));\r\n\r\n \r\nALTER TABLE ICME_PHONE_ID_MAPPING ADD(ORG_ID NUMBER);\r\nALTER TABLE icme_phone_search_bind ADD(ORG_ID NUMBER default \'1\');\r\n\r\nselect ORG_ID from  icme_phone_search_bind\r\n\r\n\r\n\r\n\r\n\r\n\r\n-- Create sequence \r\ncreate sequence ICME_FOLLOW_ORG_SEQ\r\nminvalue 1\r\nmaxvalue 9999999999999999999999999999\r\nstart with 1\r\nincrement by 1\r\ncache 20;\r\n\r\ncreate table ICME_FOLLOW_ORG\r\n(\r\n  id          INTEGER not null,\r\n  ic_code     VARCHAR2(9),\r\n  org_id      INTEGER,\r\n  create_time DATE\r\n)', '1', '', '6', '115', '2', '89', '44', '9');
