INSERT INTO `daily_report_dba` VALUES ('351', '2018-03-08', '江苏常州授权', 'select * from  imp_tmp_tab for update \r\n\r\nupdate imp_tmp_tab set col1=trim(col1),col2=trim(col2),col3=trim(col3)\r\n\r\n\r\n\r\n                                                                                                                             \r\n           --cme课程授权                                                                                                     \r\n           select * from imp_tmp_tab;                                                                                  \r\n                                                                                                                             \r\n           delete  imp_tmp_tab ;                                                                                       \r\n                       select* from ncme_course_credit c  where c.course_id=\'201600920314\'\r\n                        and organ_id=\'000000000100\'                                                                                                       \r\n           insert into ncme_course_credit                                                                                    \r\n           select t.col3,                                                                                                    \r\n           ORGAN_ID,                                                                                                         \r\n           CREDIT_TYPE,                                                                                                      \r\n           col2,                                                                                                       \r\n           EVP_VALUE,                                                                                                        \r\n           t.col1,                                                                                                           \r\n           RE_STUDY_FLAG,                                                                                                    \r\n           sysdate,                                                                                                          \r\n           COURSE_TYPE,                                                                                                      \r\n           CREDIT_YEAR,                                                                                                      \r\n           REQUEST,                                                                                                          \r\n           IS_CREDIT_SUBJECT,                                                                                                \r\n           CARD_TYPE,                                                                                                        \r\n           ICME_SUBJECT_ID                                                                                                   \r\n                                                                                                                             \r\n           from ncme_course_credit c ,imp_tmp_tab t                                                                    \r\n           where  c.course_id=\'201600920314\'\r\n                        and organ_id=\'000000000100\'             \r\n           and not exists (select 1 from  ncme_course_credit css where css.course_id=t.col3\r\n           and css.organ_id=c.organ_id and css.credit_year=\'2018\' )                                                  \r\n                                                                                                                  \r\n     select* from (\r\nselect t.col6,cs.organ_id,cs.credit_year   from ncme_course_credit cs ,imp_tmp_tab t \r\n where cs.course_id=\'201600919210\' and cs.course_serial=\'2016-003-HYS\'                                                                    \r\n) ts,(select css.course_id,css.organ_id,css.credit_year   from ncme_course_credit css )ts1\r\nwhere ts.col6=ts1.course_id\r\nand     ts.organ_id=ts1.organ_id                                                        \r\n       \r\n           \r\n  \r\n  \r\n  \r\n  \r\n                                                                                                                          \r\n           select * from ncme_course cg where cg.course_id in( select col1 from imp_tmp_tab) and cg.publish_flag<>1', '1', '', '4', '90', '2', '89', '8', '56');
