INSERT INTO `daily_report_dba` VALUES ('399', '2018-03-14', 'cme数据统计调取', '永莲;\r\n   烦请调取CME截止2017年的各专业的注册人数，也分职称和专业调取下截止2017年的学习人数，谢谢！\r\n 字段为：专业、注册人数\r\n         专业 、学习人数\r\n         职称、学习人数\r\n         \r\n         \r\n \r\nselect User_getUserInfo(re.login_name, 1) 科室,count(1)  from user_register re \r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\ngroup by User_getUserInfo(re.login_name, 1) ;\r\n\r\n\r\nselect User_getUserInfo(re.login_name, 1) 科室,count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\ngroup by User_getUserInfo(re.login_name, 1) ;\r\n\r\nselect User_getUserInfo(re.login_name, 1) 科室,count(distinct user_name) 学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\ngroup by User_getUserInfo(re.login_name, 1) ;\r\n\r\nselect User_getUserInfo(re.login_name, 2) 职称,count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\ngroup by User_getUserInfo(re.login_name, 2) ;\r\n\r\nselect User_getUserInfo(re.login_name, 2) 职称,count(distinct user_name) 学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\ngroup by User_getUserInfo(re.login_name, 2) ;', '1', '', '4', '90', '2', '89', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('400', '2018-03-14', 'cme数据统计调取历年', 'select nvl(a.职称,b.职称),nvl(a.apply_year,b.year_name) , a.申请人数,b.学习人数 from (\r\nselect User_getUserInfo(re.login_name, 2) 职称,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by User_getUserInfo(re.login_name, 2) ,apply_year) a inner join (\r\n\r\nselect User_getUserInfo(re.login_name, 2) 职称,t.year_name， count(distinct user_name)  学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\njoin cme_year t on h.startdate between t.year_begin_date and t.year_end_date+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\nand year_name in(\'2015\',\'2016\',\'2017\')\r\nand\r\nre.jobtitle_id like \'0A%\'\r\ngroup by User_getUserInfo(re.login_name, 2)，t.year_name )\r\nb  on  a.职称=b.职称\r\nand a.apply_year=b.year_name;\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nselect nvl(a.age_name,b.age_name),nvl(a.apply_year,b.year_name) , a.申请人数,b.学习人数 from (\r\nselect aa.age_name,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\n join cme_age aa on to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') between aa.begin_age and aa.end_age+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by aa.age_name,apply_year) a inner join (\r\n\r\nselect aa.age_name,t.year_name， count(distinct user_name)  学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\njoin cme_year t on h.startdate between t.year_begin_date and t.year_end_date+1\r\n join cme_age aa on to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') between aa.begin_age and aa\r\n .end_age+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\nand year_name in(\'2015\',\'2016\',\'2017\')\r\nand\r\nre.jobtitle_id like \'0A%\'\r\ngroup by aa.age_name,t.year_name )\r\nb  on  a.age_name=b.age_name\r\nand a.apply_year=b.year_name;\r\n\r\n\r\n\r\nselect nvl(a.医院级别,b.医院级别),nvl(a.apply_year,b.year_name) , a.申请人数,b.学习人数 from (\r\nselect  user_getuserinfo(re.login_name,4) 医院级别,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by  user_getuserinfo(re.login_name,4) ,apply_year) a inner join (\r\n\r\nselect  user_getuserinfo(re.login_name,4) 医院级别,t.year_name， count(distinct user_name)  学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\njoin cme_year t on h.startdate between t.year_begin_date and t.year_end_date+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\nand year_name in(\'2015\',\'2016\',\'2017\')\r\nand\r\nre.jobtitle_id like \'0A%\'\r\ngroup by  user_getuserinfo(re.login_name,4)，t.year_name )\r\nb  on  a.医院级别=b.医院级别\r\nand a.apply_year=b.year_name;\r\n\r\n\r\n\r\nselect nvl(a.学历,b.学历),nvl(a.apply_year,b.year_name) , a.申请人数,b.学习人数 from (\r\nselect User_getUserInfo(re.login_name, 3) 学历,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by User_getUserInfo(re.login_name, 3) ,apply_year) a inner join (\r\n\r\nselect User_getUserInfo(re.login_name, 3) 学历,t.year_name， count(distinct user_name)  学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\njoin cme_year t on h.startdate between t.year_begin_date and t.year_end_date+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\nand year_name in(\'2015\',\'2016\',\'2017\')\r\nand\r\nre.jobtitle_id like \'0A%\'\r\ngroup by User_getUserInfo(re.login_name, 3)，t.year_name )\r\nb  on  a.学历=b.学历\r\nand a.apply_year=b.year_name;\r\n\r\n--\r\n\r\n\r\n\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n       \r\n       \r\n       \r\n\r\nselect nvl(a.学历,b.学历),nvl(a.apply_year,b.year_name) , a.申请人数,b.学习人数 from (\r\nselect User_getUserInfo(re.login_name, 3) 学历,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by User_getUserInfo(re.login_name, 3) ,apply_year) a inner join (\r\n\r\nselect User_getUserInfo(re.login_name, 3) 学历,t.year_name， count(distinct user_name)  学习人数  from user_register re \r\njoin ncme_study_course h on h.user_name=re.login_name\r\njoin cme_year t on h.startdate between t.year_begin_date and t.year_end_date+1\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand h.startdate<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\n\r\nand year_name in(\'2015\',\'2016\',\'2017\')\r\nand\r\nre.jobtitle_id like \'0A%\'\r\ngroup by User_getUserInfo(re.login_name, 3)，t.year_name )\r\nb  on  a.学历=b.学历\r\nand a.apply_year=b.year_name;\r\n\r\n\r\n\r\n\r\n\r\nselect User_getUserInfo(re.login_name, 1) 学历,h.apply_year, count(distinct user_name) 申请人数   from user_register re \r\njoin ncme_study_credit_history h on h.user_name=re.login_name\r\nwhere re.user_reg_time<to_date(\'2018-2-1\',\'yyyy-MM-dd\') \r\nand\r\nre.jobtitle_id like \'0A%\'\r\nand apply_year in(\'2015\',\'2016\',\'2017\')\r\ngroup by User_getUserInfo(re.login_name, 1) ,apply_year\r\n\r\n\r\nselect nvl(a.subject_name,b.subject_name),\r\nnvl( a.科室,b.科室),\r\nnvl(a.apply_year,b.year_name),\r\na.申请人数,b.学习人数\r\nfrom (\r\n\r\nselect su.subject_name,\r\n       User_getUserInfo(re.login_name, 1)科室 ,\r\n       h.apply_year,\r\n       count(distinct user_name) 申请人数\r\n  from ncme_study_credit_history h\r\n  join user_register re\r\n    on re.login_name = h.user_name\r\n  join ncme_course nc\r\n    on nc.course_id = h.course_id\r\n  join ncme_subject su\r\n    on su.subject_id = nc.subject_id\r\n where re.user_reg_time < to_date(\'2018-2-1\', \'yyyy-MM-dd\')\r\n   and re.jobtitle_id like \'0A%\'\r\n   and apply_year in (\'2015\', \'2016\', \'2017\')\r\n group by su.subject_name, User_getUserInfo(re.login_name, 1), h.apply_year) a \r\nfull join \r\n (\r\n \r\n \r\n \r\nselect su.subject_name,su.subject_id,su.subject2_id\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n       y.year_name,\r\n       count(distinct user_name) 学习人数\r\n  from ncme_study_course h\r\n  join user_register re\r\n    on re.login_name = h.user_name\r\n  join ncme_course nc\r\n    on nc.course_id = h.course_id\r\n  join ncme_subject su\r\n    on su.subject_id = nc.subject_id\r\n    join cme_year y on h.startdate between y.year_begin_date and y.year_end_date +1\r\n where re.user_reg_time < to_date(\'2018-2-1\', \'yyyy-MM-dd\')\r\n   and re.jobtitle_id like \'0A%\'\r\n   and year_name in (\'2015\', \'2016\', \'2017\')\r\n group by su.subject_name, User_getUserInfo(re.login_name, 1), y.year_name) b\r\n on a.subject_name=b.subject_name\r\n and a.科室=b.科室\r\n and a.apply_year=b.year_name', '0', '', '4', '90', '2', '91', '8', '241');
INSERT INTO `daily_report_dba` VALUES ('401', '2018-03-14', '修改数据', 'delete from scott.imp_tmp_tab\r\ninsert into  scott.imp_tmp_tab (col1,col2)\r\n      select  user_id,count(*)\r\n          from em_score_history t\r\n         where t.exam_id = 8681\r\n           and t.user_id in( select   student_id  from  em_number_student  e\r\n           where e.exam_id = 8681\r\n  -- and e.state = 2\r\n         )\r\n           group by user_id;\r\n           \r\n           \r\n   merge into em_number_student e \r\n   using scott.imp_tmp_tab t on (e.student_id=t.col1)\r\n   when matched then update set e.seat=t.col2\r\n  where e.exam_id = 8681\r\n   and e.state = 2; \r\n   \r\n   \r\n     update em_number_student t\r\n      set t.seat = 1\r\n    where t.exam_id = 8681\r\n      and t.seat > 2', '1', '', '75', '102', '2', '89', '39', '9');
