INSERT INTO `daily_report_dba` VALUES ('415', '2018-03-16', '修改题目', 'select * from   imp_tmp_tab for update \r\n\r\nupdate imp_tmp_tab set col1=trim(col1)\r\n\r\nselect * from ncme_question\r\n\r\nselect q.quescont,\r\nq.answcont,\r\nq.answer,\r\ninstr(q.answcont,q.answer,1),substr(answcont,instr(q.answcont,answer,1)),\r\ninstr(substr(answcont,instr(q.answcont,answer,1)),\'<br>\',1)+instr(q.answcont,q.answer,1),\r\ndecode(instr(substr(answcont,instr(q.answcont,answer,1)),\'<br>\',1),0,1000,instr(substr(answcont,instr(q.answcont,answer,1)),\'<br>\',1)),\r\nsubstr(answcont,instr(q.answcont,answer,1),decode(instr(substr(answcont,instr(q.answcont,answer,1)+instr(q.answcont,q.answer,1)),\'<br>\',1),0,1000,instr(substr(answcont,instr(q.answcont,answer,1)),\'<br>\',1))-instr(q.answcont,answ_num,1)-1)\r\n\r\n from ncme_question q where q.course_id in(select col1 from imp_tmp_tab)\r\n delete from imp_tmp_tab\r\n insert into imp_tmp_tab(col1,col2,col8,col6,col7,col9)\r\n select q.question_id,q.quescont,\r\nq.answcont,\r\nq.answer,\r\ninstr(q.answcont,q.answer||\'.\',1),substr(answcont,instr(q.answcont,q.answer||\'.\',1))\r\n from ncme_question q where q.course_id in(select col1 from imp_tmp_tab)\r\n \r\n update  imp_tmp_tab set col10=col9  where col2 is not null \r\n and col9 not like \'%<br>%\';\r\n \r\n select col2,col6,col8,col9 , \r\n instr(col9,\'<br>\',1),\r\n substr(col8,col7 ,decode(instr(col9,\'<br>\',1),0,1000,instr(col9,\'<br>\',1))-1)\r\n from imp_tmp_tab q where col2 is not null \r\n and col9  like \'%<br>%\';\r\n select * from imp_tmp_tab for update \r\nupdate \r\n imp_tmp_tab q \r\n set col10= substr(col8,col7 ,decode(instr(col9,\'<br>\',1),0,1000,instr(col9,\'<br>\',1))-1)\r\n where col2 is not null \r\n and col9  like \'%<br>%\';\r\nselect * from    imp_tmp_tab where col2 is not null\r\n and substr(col10,0,1)<>col6;\r\nselect  col1,col2,col6,col8,col9 ,substr(col8,instr(q.col8,q.col6,1) ,decode(instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1),0,1000,instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1))-1)\r\nfrom imp_tmp_tab q\r\n where col2 is not null \r\n  and substr(col10,0,1)<>col6;\r\nupdate   imp_tmp_tab q set col10= substr(col8,instr(q.col8,q.col6,1) ,decode(instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1),0,1000,instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1))-1)\r\n\r\n where col2 is not null \r\n  and substr(col10,0,1)<>col6;\r\n\r\nupdate imp_tmp_tab set col11= col2||substr(col10,3)   where col2 is not null;\r\n\r\nmerge into ncme_question q  using  (select * from  imp_tmp_tab where col2 is not null)\r\nt \r\non (t.col1=q.question_id)\r\nwhen matched then update set q.interpret=t.col11', '1', '', '4', '90', '2', '97', '32', '9');
