INSERT INTO `daily_report_dba` VALUES ('426', '2018-03-19', '上线脚本', '--ICME_PHONE_BASE_SET\r\nALTER TABLE icme_phone_base_set ADD CAN_SET NUMBER(1) default \'1\' not NULL ;\r\nALTER TABLE icme_phone_base_set ADD MAX_SIGN_NUM NUMBER default \'2\' not null ;\r\ncomment on column ICME_PHONE_BASE_SET.CAN_SET\r\n  is \'是否允许微信端进行签到设置：1是，0否\';\r\ncomment on column ICME_PHONE_BASE_SET.MAX_SIGN_NUM\r\n  is \'最大签到次数，默认值2\';\r\n  \r\n  \r\n--ICME_SIGN_RECORD \r\ncreate table ICME_SIGN_RECORD\r\n(\r\n  ID          INTEGER not null,\r\n  IC_CODE     VARCHAR2(9) not null,\r\n  PHONE_ID    VARCHAR2(40),\r\n  SUBJECT_ID  INTEGER not null,\r\n  STATUS      NUMBER(1) default \'0\' not null,\r\n  HAS_SCORE   NUMBER(1) default \'0\' not null,\r\n  CREATE_TIME DATE default SYSDATE not null,\r\n  UPDATE_TIME DATE not null,\r\n  PRIMARY KEY (ID)\r\n);\r\ncomment on column ICME_SIGN_RECORD.IC_CODE\r\n  is \'签到人员IC卡号\';\r\ncomment on column ICME_SIGN_RECORD.PHONE_ID\r\n  is \'签到人员所绑定微信ID\';\r\ncomment on column ICME_SIGN_RECORD.SUBJECT_ID\r\n  is \'签到课题\';\r\ncomment on column ICME_SIGN_RECORD.STATUS\r\n  is \'签到状态：0签到次数不完整，1签到成功\';\r\ncomment on column ICME_SIGN_RECORD.HAS_SCORE\r\n  is \'是否成功授分：0未授分，1已授分\';\r\ncomment on column ICME_SIGN_RECORD.CREATE_TIME\r\n  is \'创建时间\';\r\ncomment on column ICME_SIGN_RECORD.UPDATE_TIME\r\n  is \'最后更新时间\';\r\nCREATE SEQUENCE icme_sign_record_seq;\r\ncreate index SIGN_RECORD_IC_IND on ICME_SIGN_RECORD (IC_CODE);\r\ncreate index SIGN_RECORD_SUBID_IND on ICME_SIGN_RECORD (SUBJECT_ID);\r\n\r\n--ICME_SIGN_RECORD_DETAIL\r\ncreate table ICME_SIGN_RECORD_DETAIL\r\n(\r\n  ID         NUMBER not null,\r\n  IC_CODE    VARCHAR2(9) not null,\r\n  SUBJECT_ID INTEGER not null,\r\n  CURR_TIMES NUMBER(1) not null,\r\n  SIGN_DATE  DATE not null,\r\n  PRIMARY KEY (ID)\r\n) ;\r\ncomment on column ICME_SIGN_RECORD_DETAIL.IC_CODE\r\n  is \'签到人员IC卡号\';\r\ncomment on column ICME_SIGN_RECORD_DETAIL.SUBJECT_ID\r\n  is \'签到课题\';\r\ncomment on column ICME_SIGN_RECORD_DETAIL.CURR_TIMES\r\n  is \'当前为第几次签到记录\';\r\ncomment on column ICME_SIGN_RECORD_DETAIL.SIGN_DATE\r\n  is \'签到时间\';\r\nCREATE SEQUENCE icme_sign_record_detail_seq;  \r\ncreate index SIGN_RECORD_DETAIL_ICSUB_IND on ICME_SIGN_RECORD_DETAIL (IC_CODE, SUBJECT_ID);\r\ncreate unique index SIGN_RECORD_DETAIL_UNIQUE on ICME_SIGN_RECORD_DETAIL (IC_CODE, SUBJECT_ID, CURR_TIMES);\r\n\r\n\r\n\r\n--ICME_SIGN_TIME_SET\r\n/*create table ICME_SIGN_TIME_SET\r\n(\r\n  ID           NUMBER not null,\r\n  SUBJECT_ID   NUMBER not null,\r\n  SIGN_NUM     NUMBER(1) not null,\r\n  ADMIN_ID     INTEGER not null,\r\n  LOCATION_LNG NUMBER(10,7),\r\n  LOCATION_LAT NUMBER(10,7),\r\n  SIGN_RANGE   INTEGER,\r\n  CREATE_TIME  DATE default SYSDATE not null,\r\n  UPDATE_TIME  DATE not null,\r\n  PRIMARY KEY (ID)\r\n);*/\r\ncomment on column ICME_SIGN_TIME_SET.SUBJECT_ID\r\n  is \'课题Id\';\r\ncomment on column ICME_SIGN_TIME_SET.SIGN_NUM\r\n  is \'签到次数，与ICME_SIGN_TIME_SET_DEATIL记录数相同\';\r\ncomment on column ICME_SIGN_TIME_SET.ADMIN_ID\r\n  is \'设置该签到记录的账号ID\';\r\ncomment on column ICME_SIGN_TIME_SET.LOCATION_LNG\r\n  is \'经度\';\r\ncomment on column ICME_SIGN_TIME_SET.LOCATION_LAT\r\n  is \'纬度\';\r\ncomment on column ICME_SIGN_TIME_SET.SIGN_RANGE\r\n  is \'允许距离签到点范围(米)\';\r\ncomment on column ICME_SIGN_TIME_SET.CREATE_TIME\r\n  is \'创建时间\';\r\ncomment on column ICME_SIGN_TIME_SET.UPDATE_TIME\r\n  is \'最后更新时间\';/*\r\nCREATE SEQUENCE icme_sign_time_set_seq; */ \r\n/*create unique index SIGN_SUBJECTID_IND on ICME_SIGN_TIME_SET (SUBJECT_ID);*/\r\n\r\n\r\n\r\n/*create table ICME_SIGN_TIME_SET_DETAIL\r\n(\r\n  ID         NUMBER not null,\r\n  SUBJECT_ID NUMBER not null,\r\n  THIS_TIMES NUMBER not null,\r\n  START_DATE DATE not null,\r\n  END_DATE   DATE not null,\r\n  PRIMARY KEY (ID)\r\n);*/\r\ncomment on column ICME_SIGN_TIME_SET_DETAIL.ID\r\n  is \'附属表主键\';\r\ncomment on column ICME_SIGN_TIME_SET_DETAIL.SUBJECT_ID\r\n  is \'课题ID 冗余\';\r\ncomment on column ICME_SIGN_TIME_SET_DETAIL.THIS_TIMES\r\n  is \'第n次签到\';\r\ncomment on column ICME_SIGN_TIME_SET_DETAIL.START_DATE\r\n  is \'第n次签到的开始时间\';\r\ncomment on column ICME_SIGN_TIME_SET_DETAIL.END_DATE\r\n  is \'第n次签到的结束时间\';/*\r\nCREATE SEQUENCE icme_sign_time_set_detail_seq;  \r\ncreate index SIGN_DETAIL_SUBJECTID_IND on ICME_SIGN_TIME_SET_DETAIL (SUBJECT_ID);\r\ncreate unique index SIGN_DETAIL_SUB_TIMES_IND on ICME_SIGN_TIME_SET_DETAIL (SUBJECT_ID, THIS_TIMES);       \r\n  */', '1', '', '6', '115', '2', '114', '8', '9');
