INSERT INTO `daily_report_dba` VALUES ('444', '2018-03-21', '烦请将2014-2017年涉及河北所有卡类型的的开卡张数和绑卡张数调取下，同时调取下保定地区的按年度的绑卡张数，谢谢!\r\n字段为：年度、卡类型中英文、开卡数、绑卡数、保定地区绑卡数', 'select substr(po.card_type, 0, 4),pc.card_type,pc.card_name,count(1) ,count(distinct user_name) ,\r\ncount(distinct re.login_name) \r\n  from ncme_study_card_history hh\r\n  left join user_register re  on  re.login_name=hh.user_name  and re.user_x_id like \'12002%\'\r\n  join ncme_paycard_organ po\r\n    on po.card_type = hh.card_type\r\n  join ncme_pay_card pc\r\n    on pc.card_type = po.card_type\r\n where po.organ_id in (select organ_id\r\n                         from ncme_admin_organ o\r\n                        where o.district_id like \'12%\')\r\n   and substr(po.card_type, 0, 4) >= \'2014\'\r\n   and substr(po.card_type, 0, 4) <= \'2018\'\r\n  group by substr(po.card_type, 0, 4),pc.card_type,pc.card_name', '1', '', '4', '90', '2', '91', '8', '56');
