INSERT INTO `daily_report_dba` VALUES ('449', '2018-03-22', 'sql编写', 'merge into SRP_ORG_INFO ss using (\r\nselect t2.id, t1.maxnum + t2.rm renum\r\n  from (select authority_user_id, nvl(max(RECORD_NUM), 0) maxnum\r\n          from SRP_ORG_INFO o2\r\n         group by authority_user_id) t1\r\n  join (select authority_user_id,\r\n               id,\r\n               row_number() over(partition by authority_user_id order by id) rm\r\n        \r\n          from SRP_ORG_INFO o\r\n         where RECORD_NUM is null) t2\r\n    on t1.authority_user_id = t2.authority_user_id) aa\r\n    on (aa.id=ss.id)\r\n    when matched then update set ss.record_num=aa.renum,\r\n     ss.audit_status=2\r\n     where id in(61,62)', '1', '', '219', '225', '2', '97', '50', '9');
INSERT INTO `daily_report_dba` VALUES ('450', '2018-03-22', 'sql编写', '-- Create table\r\ncreate table REGULAR_ORG_INFO_RELATION\r\n(\r\n  regular_org_info_id     INTEGER not null,\r\n  regular_org_info_new_id INTEGER\r\n);\r\n-- Add comments to the table \r\ncomment on table REGULAR_ORG_INFO_RELATION\r\n  is \'REGULAR_ORG_INFO新年度开启后的关系表\';\r\n-- Add comments to the columns \r\ncomment on column REGULAR_ORG_INFO_RELATION.regular_org_info_new_id\r\n  is \'由于不太清楚原表中的org_type为2时怎么对应机构关系 现在暂时用中间表手动维护新旧年度下这个表的关系 为了查出新年度下的id\';\r\n\r\n\r\n\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106320, 106566);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106321, 106567);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106340, 106568);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106460, 106569);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106327, 106565);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106322, 106560);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106326, 106564);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106323, 106561);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106343, 106558);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106325, 106563);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106324, 106562);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106381, 106559);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106400, 106553);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106360, 106554);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106380, 106556);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106401, 106557);\r\n\r\ninsert into regular_org_info_relation (REGULAR_ORG_INFO_ID, REGULAR_ORG_INFO_NEW_ID)\r\nvalues (106480, 106555);\r\n\r\n\r\nalter table REGULAR_PERSONNEL_INFO add year_id int;\r\nupdate REGULAR_PERSONNEL_INFO set year_id=2016;\r\n\r\ninsert into REGULAR_PERSONNEL_INFO\r\n  (REGULAR_PERSONNEL_INFO_ID,\r\n   ORG_REGULAR_REGISTER_INFO_ID,\r\n   PERSONNEL_ID,\r\n   PERSONNEL_TYPE,\r\n   MEDICAL_REG_INFO_ID,\r\n   REGULAR_ORG_INFO_ID,\r\n   REGULAR_STEP_ID,\r\n   EXAM_YEARLY_PLAN_ID,\r\n   APPLY_EASY_PROCESSES,\r\n   PROFESSIONAL_SKILL_RESULT,\r\n   MORALITY_RESULT,\r\n   ACHIEVEMENTS_RESULT,\r\n   REGULAR_RESULT,\r\n   CREATE_TIME,\r\n   CREATER,\r\n   ENABLE,\r\n   SELF_MORALITY_RESULT,\r\n   SELF_ACHIEVEMENTS_RESULT, \r\n   SYNCHRONIZE_STATE,\r\n   YEAR_ID\r\n   \r\n   )\r\n  select s_regular_personnel_info.nextval,\r\n         orri2.org_regular_register_info_id,\r\n         dbi2. doctor_base_info_id,\r\n         rpi.personnel_type,\r\n         mri2.medical_reg_info_id,\r\n         roie.regular_org_info_new_id,\r\n         rpi.regular_step_id,\r\n         eyp.exam_yearly_plan_id,         \r\n         0,\r\n         0,\r\n         0,\r\n         0,\r\n         0,\r\n         sysdate,         \r\n         rpi.creater,\r\n         rpi.enable,\r\n         0,\r\n         0,\r\n         rpi.synchronize_state,\r\n         2018 \r\n    from EXAM_YEARLY_PLAN eyp, REGULAR_PERSONNEL_INFO rpi\r\n    join doctor_base_info dbi\r\n      on rpi.personnel_id = dbi.doctor_base_info_id\r\n    join doctor_base_info dbi2\r\n      on dbi2.sys_employee_id = dbi.sys_employee_id\r\n      and dbi2.doctor_base_info_id <> dbi.doctor_base_info_id\r\n      and dbi2.year_id = 2018\r\n    join medical_reg_info mri\r\n      on mri.medical_reg_info_id = rpi.medical_reg_info_id\r\n    join medical_reg_info mri2\r\n      on mri2.medical_register_number = mri.medical_register_number\r\n      and mri2.year_id = 2018\r\n    join ORG_REGULAR_REGISTER_INFO orri\r\n      on orri.org_regular_register_info_id =rpi.org_regular_register_info_id\r\n      and orri.medical_reg_info_id = mri.medical_reg_info_id\r\n    join ORG_REGULAR_REGISTER_INFO orri2\r\n      on orri2.medical_reg_info_id = mri2.medical_reg_info_id  \r\n    join regular_org_info roi\r\n      on roi.regular_org_info_id = rpi.regular_org_info_id\r\n    join regular_org_info_relation roie\r\n      on roie.regular_org_info_id = roi.regular_org_info_id  \r\n   where eyp.enable = 2;', '1', '', '66', '129', '2', '97', '40', '9');
