INSERT INTO `daily_report_dba` VALUES ('492', '2018-03-28', '复制课件', 'declare\r\nbegin\r\n  \r\n  for ro in(select * from imp_tmp_tab) loop\r\n\r\n\r\n\r\ninsert into NCME_COURSE\r\nselect COL6,\r\nINSTITUTE_ID,COL3，\r\nEDITOR,\r\nWEIGHT,SYSDATE,\r\nIS_NEW,\r\nPUBLISH_FLAG,\r\nBACKDROP,\r\nCW_COUNT,\r\nGUIDE,\r\nFEEDBACK_FLAG,\r\nCOURSE_TYPE,\r\nLIST_ID,\r\nCLICK_TIMES,\r\nTOTAL_CLICK_TIMES,\r\nSUBJECT_ID,\r\nPUBLISH_DATE,\r\nIS_RURAL,\r\nIS_CITY,\r\nREMARK,\r\nOLD_COURSE_ID,\r\nRESPON_NAME,\r\nRESPON_UNIT\r\n from NCME_COURSE CS JOIN imp_tmp_tab T\r\nON T.COL2=CS.COURSE_ID\r\n\r\n  --复制课程负责人1\r\n  insert into ncme_course_Expert(course_id,Exp_Id)\r\n  SELECT ro.col6,exp_id FROM ncme_course_Expert WHERE course_id=ro.col2;\r\n\r\n  --复制课件1\r\n     insert into ncme_courseware(courseware_id,course_id,title,exp_id,brief,weight,insdate,product_id,\r\n            keyword1,keyword2,keyword3,keyword4,keyword5,keyword6,keyword7,keyword8,keyword9,keyword10,\r\n            type,a_times,b_times,c_times,is_ques)\r\n      SELECT courseware_id,ro.col6,title,exp_id,brief,weight,insdate,product_id,\r\n            keyword1,keyword2,keyword3,keyword4,keyword5,keyword6,keyword7,keyword8,keyword9,keyword10,\r\n            type,0 a_times,0 b_times,0 c_times,is_ques\r\n       FROM ncme_courseware WHERE course_id=ro.col2;\r\n\r\n\r\n  --复制考题\r\n    insert into ncme_question(question_id,course_id,courseware_id,answ_num,quescont,answcont,answer,interpret)\r\n           SELECT ncme_question_seq.nextval,ro.col6,courseware_id,answ_num,quescont,answcont,answer,interpret\r\n           FROM ncme_question WHERE course_id=ro.col2;\r\n\r\n  --复制学科授权\r\n    insert into ncme_course_subject(course_id,subject_id,weight)\r\n           SELECT ro.col6,subject_id,weight\r\n           FROM ncme_course_subject WHERE course_id=ro.col2;\r\n\r\n\r\n  --复制系统授权\r\n    insert into ncme_course_system(course_id,sys_Id,weight)\r\n           SELECT ro.col6,sys_Id,weight\r\n           FROM ncme_course_system WHERE course_id=ro.col2;\r\n\r\n\r\n\r\n end loop;\r\nend;\r\n\r\nselect * from imp_tmp_tab\r\n\r\nselect *\r\n  from ncme_courseware cs\r\n where cs.course_id in (select col6 from imp_tmp_tab)', '1', '', '4', '90', '2', '89', '8', '212');
INSERT INTO `daily_report_dba` VALUES ('493', '2018-03-28', '修改题目', 'select * from   imp_tmp_tab where 1=2 for update \r\n\r\ndelete from imp_tmp_tab where col7 is not null\r\nupdate imp_tmp_tab set col1=col6\r\nupdate imp_tmp_tab set col1=trim(col1)\r\n\r\nselect * from ncme_question\r\n\r\nselect  q.quescont,\r\nq.answcont,\r\nq.answer,interpret\r\n from ncme_question q where q.course_id in(select col1 from imp_tmp_tab)\r\n \r\n \r\n \r\n delete from imp_tmp_tab\r\n insert into imp_tmp_tab(col1,col2,col8,col6,col7,col9)\r\n select q.question_id,q.quescont,\r\nq.answcont,\r\nq.answer,\r\ninstr(q.answcont,q.answer||\'.\',1),substr(answcont,instr(q.answcont,q.answer||\'.\',1))\r\n from ncme_question q where q.course_id in(select col1 from imp_tmp_tab)\r\n \r\n update  imp_tmp_tab set col10=col9  where col2 is not null \r\n and col9 not like \'%<br>%\';\r\n \r\n select col2,col6,col8,col9 , \r\n instr(col9,\'<br>\',1),\r\n substr(col8,col7 ,decode(instr(col9,\'<br>\',1),0,1000,instr(col9,\'<br>\',1))-1)\r\n from imp_tmp_tab q where col2 is not null \r\n and col9  like \'%<br>%\';\r\n select * from imp_tmp_tab for update \r\nupdate \r\n imp_tmp_tab q \r\n set col10= substr(col8,col7 ,decode(instr(col9,\'<br>\',1),0,1000,instr(col9,\'<br>\',1))-1)\r\n where col2 is not null \r\n and col9  like \'%<br>%\';\r\nselect * from    imp_tmp_tab where col2 is not null\r\n and substr(col10,0,1)<>col6;\r\nselect  col1,col2,col6,col8,col9 ,substr(col8,instr(q.col8,q.col6,1) ,decode(instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1),0,1000,instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1))-1)\r\nfrom imp_tmp_tab q\r\n where col2 is not null \r\n  and substr(col10,0,1)<>col6;\r\nupdate   imp_tmp_tab q set col10= substr(col8,instr(q.col8,q.col6,1) ,decode(instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1),0,1000,instr(substr(col8,instr(q.col8,q.col6,1)),\'<br>\',1))-1)\r\n\r\n where col2 is not null \r\n  and substr(col10,0,1)<>col6;\r\n\r\nupdate imp_tmp_tab set col11= col2||substr(col10,3)   where col2 is not null;\r\n\r\nmerge into ncme_question q  using  (select * from  imp_tmp_tab where col2 is not null)\r\nt \r\non (t.col1=q.question_id)\r\nwhen matched then update set q.interpret=t.col11\r\n  where col7 is not null\r\n  \r\n  \r\n  select * from', '1', '', '4', '90', '2', '97', '32', '212');
INSERT INTO `daily_report_dba` VALUES ('494', '2018-03-29', '删除承德授权', 'select * from user_district d where d.district_name like \'%河南%\'\r\n\r\n永莲 烦请查一下河南省的CME课程类别的市级二类有多少人学习 多少人申请学分了，谢谢\r\n陈红叶  8:50:27\r\nselect * from user_district d where d.parent_id=\'12\'\r\n\r\n永莲，烦请查一下以下课题在承德地区今年是否有申请学分或学习中数据，谢谢！\r\n201600920039\r\n201500011581\r\n201500011424\r\n201500011205\r\n201500011073\r\n201500011054\r\n201400010798\r\n201300009255\r\n201000007897\r\n永莲\r\n\r\n\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,\r\n     \r\n      case re.user_gender\r\n           when \'f\' then\r\n            \'女\'\r\n           when \'m\' then\r\n            \'男\'\r\n         end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n  \r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       User_getUserInfo(re.login_name, 3) 学历,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n    sc.card_no 付费卡号,\r\n      --nc.name 课程名称,\r\n sc.apply_subtype 申请子类型,\r\nsc.startdate 开始日期,\r\n sc.passdate 通过日期,\r\n --sc.applydate 申请日期,\r\n      sc.senddate 发证日期\r\n     -- sc.apply_credit  学分,\r\n     -- sc.apply_credit*3 学时,\r\n      --ct.credit_name 学分类型\r\n  --select count(*)\r\n    from ncme_study_credit sc\r\n    join user_register re on re.login_name=sc.user_name\r\n    join ncme_course_credit css on css.course_id=sc.course_id\r\n    and css.organ_id=sc.organ_id\r\n    join ncme_admin_organ o on o.organ_id=css.organ_id\r\n    \r\n     where sc.user_x_id like \'12005%\'\r\nand sc.course_id in(\r\n\'201600920039\',\r\n\'201500011581\',\r\n\'201500011424\',\r\n\'201500011205\',\r\n\'201500011073\',\r\n\'201500011054\',\r\n\'201400010798\',\r\n\'201300009255\',\r\n\'201000007897\'\r\n)\r\n--and css.course_type=0\r\nand o.district_id like \'12005%\'\r\n\r\n\r\ndelete from  ncme_course_credit sc where sc.course_id in(\r\n\'201600920039\',\r\n\'201500011581\',\r\n\'201500011424\',\r\n\'201500011205\',\r\n\'201500011073\',\r\n\'201500011054\',\r\n\'201400010798\',\r\n\'201300009255\',\r\n\'201000007897\'\r\n)and organ_id=\'000000000126\';\r\n\r\n\r\ndelete from  ncme_paycard_course where card_type=\'2018hbcdel6f\'\r\nand course_id in(\r\n\'201600920039\',\r\n\'201500011581\',\r\n\'201500011424\',\r\n\'201500011205\',\r\n\'201500011073\',\r\n\'201500011054\',\r\n\'201400010798\',\r\n\'201300009255\',\r\n\'201000007897\'\r\n)', '1', '', '4', '90', '2', '97', '8', '56');
