INSERT INTO `daily_report_dba` VALUES ('506', '2018-03-29', '数据调取', 'select * from ncme_pay_card pc where pc.card_type like \'2017%\';\r\n\r\n\r\nselect * from (\r\nselect d.district_name,substr( ca.card_type,0,4) year_name,count(1) cn  from ncme_paycard_organ o\r\njoin (\r\nselect * from ncme_study_card_history \r\nwhere  user_name is not null\r\nunion \r\nselect * from  ncme_study_card \r\nwhere  user_name is not null) ca on ca.card_type=o.card_type\r\njoin ncme_admin_organ oo on o.organ_id=oo.organ_id\r\njoin user_district d on d.district_id=oo.district_id\r\n where oo.district_id like \'27%\'\r\nand( o.card_type like \'2017%\'or o.card_type like \'2018%\')\r\ngroup by d.district_name,substr( ca.card_type,0,4))\r\npivot (max(cn) for year_name in(\'2017\',\'2018\') )\r\n\r\nselect count(1) from ncme_study_card where card_type  in(\r\nselect o.card_type from ncme_paycard_organ o \r\nwhere ( o.card_type like \'2018%\')\r\nand organ_id=\'000000000110\'\r\n\r\n)\r\n\r\nselect * from ncme_admin_organ where district_id like \'27%\'\r\n\r\n\r\nselect *\r\n  from (select user_getdistrict(re.user_x_id, 2) 市,\r\n               to_char(ca.link_date, \'yyyy\') year_name,\r\n               count(1) cn\r\n          from ncme_paycard_organ o\r\n          join (select *\r\n                 from ncme_study_card_history\r\n                where user_name is not null\r\n               union\r\n               select * from ncme_study_card where user_name is not null) ca\r\n            on ca.card_type = o.card_type\r\n          join ncme_admin_organ oo\r\n            on o.organ_id = oo.organ_id\r\n          join user_district d\r\n            on d.district_id = oo.district_id\r\n          join user_register re\r\n            on re.login_name = ca.user_name\r\n         where oo.district_id like \'27%\'\r\n           and re.user_x_id like d.district_id || \'%\'\r\n           and (o.card_type like \'2017%\' or o.card_type like \'2018%\')\r\n         group by user_getdistrict(re.user_x_id, 2),\r\n                  to_char(ca.link_date, \'yyyy\')) pivot(max(cn) for year_name in(\'2017\',\r\n                                                                                \'2018\'))\r\n                                                                                \r\n                                                                                \r\nselect *\r\n  from (\r\nselect user_getdistrict(re.user_x_id, 2),\r\n                 nvl(to_char(ca.link_date, \'yyyy\'),\'没有邦卡时间\') year_name ,count(1) cn  from (select *\r\n                 from ncme_study_card_history\r\n                where user_name is not null\r\n               union\r\n               select * from ncme_study_card where user_name is not null) ca \r\n              join user_register re\r\n            on re.login_name = ca.user_name  and user_x_id like \'27%\'\r\n            where  (ca.card_type like \'2017%\' or ca.card_type like \'2018%\') \r\n            group by user_getdistrict(re.user_x_id, 2),\r\n                  to_char(ca.link_date, \'yyyy\')) pivot(max(cn) for year_name in(\'2017\',\r\n                                                                                \'2018\',\'没有邦卡时间\'))', '1', '', '4', '90', '2', '97', '32', '53');
