INSERT INTO `daily_report_dba` VALUES ('520', '2018-03-30', '神经内科数据统计', 'select cc.credit_num,e.exp_name,nc.name,NC.COURSE_ID,count( distinct cs.user_name)\r\n  from (/*\r\n  select user_name，course_id,organ_id,H.APPLY_YEAR， rownum from ncme_study_credit_history h where\r\n  h.credit_type=\'G1\'\r\n  and \r\nh.course_id in(\r\nselect course_id from ncme_course where subject_id = \'02-07\'\r\n) and apply_year in(\'2017\',\'2016\')\r\n\r\n   union ALL*/\r\n\r\n select user_name，course_id,organ_id,APPLY_YEAR ,rownum from  ncme_study_credit\r\n where  credit_type=\'G1\' ) cs\r\n  join ncme_course_credit cc\r\n    on cs.course_id = cs.course_id\r\nAND CC.CREDIT_YEAR= CS.APPLY_YEAR\r\n    and cc.credit_type=\'G1\'\r\n  join ncme_course nc\r\n    on nc.course_id = cs.course_id\r\n    join ncme_course_expert ee on ee.course_id=nc.course_id\r\n    join ncme_expert e on e.exp_id=ee.exp_id\r\n where nc.subject_id = \'02-07\'\r\n group by cc.credit_num,e.exp_name,nc.name,NC.COURSE_ID\r\n \r\n \r\n \r\n \r\n \r\n select cc.credit_num,e.exp_name,nc.name,NC.COURSE_ID,count( distinct cs.user_name)\r\n  from (/*\r\n  select user_name，course_id,organ_id,H.APPLY_YEAR， rownum from ncme_study_credit_history h where\r\n  h.credit_type=\'G1\'\r\n  and \r\nh.course_id in(\r\nselect course_id from ncme_course where subject_id = \'02-07\'\r\n) and apply_year in(\'2017\',\'2016\')\r\n\r\n   union ALL*/\r\n\r\n select user_name，course_id from  ncme_study_course\r\n where startdate>=to_date(\'2018-2-1\',\'yyyy-MM-dd\') ) cs\r\n  join ncme_course_credit cc\r\n    on cs.course_id = cs.course_id\r\n--AND CC.CREDIT_YEAR= CS.APPLY_YEAR\r\n    and cc.credit_type=\'G1\'\r\n  join ncme_course nc\r\n    on nc.course_id = cs.course_id\r\n    join ncme_course_expert ee on ee.course_id=nc.course_id\r\n    join ncme_expert e on e.exp_id=ee.exp_id\r\n where nc.subject_id = \'02-07\'\r\n group by cc.credit_num,e.exp_name,nc.name,NC.COURSE_ID\r\n \r\n select * from ncme_study_course\r\n \r\n \r\n \r\n \r\n \r\