INSERT INTO `daily_report_dba` VALUES ('560', '2018-04-10', '商务数据调取 各地区二级医院数 肿瘤 内科 外科 医生数以及主任医生数', 'select * from   IMP_TMP_TAB for update \r\n\r\n肿瘤科      内科      外科    \r\n医院数量  医生总会员数（主任医师、副主任医师、主治医师、住院医师、医士）  科主任会员数（主任医师、副主任医师）  医院数量  医生总会员数（主任医师、副主任医师、主治医师、住院医师、医士）  科主任会员数（主任医师、副主任医师）  医院数量  医生总会员数（主任医师、副主任医师、主治医师、住院医师、医士）	科主任会员数（主任医师、副主任医师）\r\n\r\nselect * from user_jobtitle_define\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id like \'0A%\'\r\nand User_getUserInfo(re.login_name, 1)=\'肿瘤科\'\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col11=aa.cnt; \r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id  in (\'0A000002\',\'0A000001\')\r\nand User_getUserInfo(re.login_name, 1)=\'肿瘤科\'\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col12=aa.cnt; \r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(distinct re.d_id) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand User_getUserInfo(re.login_name, 1)=\'肿瘤科\'\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set spec=aa.cnt; \r\n\r\n\r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id  in (\'0A000002\',\'0A000001\')\r\nand User_getUserInfo(re.login_name, 1)in(\'普通外科\',\'外科\')\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col14=aa.cnt; \r\n\r\n\r\n\r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id  like \'0A%\'\r\nand User_getUserInfo(re.login_name, 1)in(\'普通外科\',\'外科\')\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col15=aa.cnt; \r\n\r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(distinct re.d_id) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand User_getUserInfo(re.login_name, 1)in(\'普通外科\',\'外科\')\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set orgid=aa.cnt; \r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id  in (\'0A000002\',\'0A000001\')\r\nand User_getUserInfo(re.login_name, 1)in(\'内科其他\',\'内科\')\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col4=aa.cnt; \r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(1) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand re.jobtitle_id  like \'0A%\'\r\nand User_getUserInfo(re.login_name, 1)in(\'内科其他\',\'内科\')\r\ngroup by col9 ) aa on （aa.col9=tt.col9）\r\nwhen matched then update set col5=aa.cnt; \r\n\r\n\r\nmerge into IMP_TMP_TAB tt using (\r\nselect col9,count(distinct re.d_id) cnt  from USER_REGISTER RE JOIN IMP_TMP_TAB T\r\nON  re.user_x_id like t.col9||\'%\'\r\nwhere user_getuserinfo(re.login_name, 4)=\'二级\'\r\nand User_getUserInfo(re.login_name, 1)in (\'内科其他\',\'内科\')\r\ngroup by col9) aa on （aa.col9=tt.col9）\r\nwhen matched then update set adminid=aa.cnt; \r\n\r\n\r\n\r\nselect col1,\r\n       col2,\r\n       col3,\r\n       col6,\r\n       col7,\r\n       col8,\r\n       col9,\r\n       col10, spe