INSERT INTO `daily_report_dba` VALUES ('579', '2018-04-12', '卡号段数据调取\r\n\r\n    烦请以下号段按照号段将使用情况导出来，谢谢！\r\n8317219501-8317220000\r\n8317195001-8317195500 \r\n8317163501-8317164000 \r\n8317161501-8317162000 \r\n8317158001-8317158500 \r\n8317157501-8317158000\r\n字段为：学习卡、卡类型、总分值、剩余分值、绑定用户名、是否有效', 'select * from   imp_tmp_tab  for update \r\n\r\n\r\nupdate imp_tmp_tab set col3=substr(trim(col1),-10);\r\n\r\nupdate imp_tmp_tab set col2=substr(trim(col1),0,10);\r\n\r\n：学习卡、卡类型、总分值、剩余分值、绑定用户名、是否有效\r\nselect ca.card_no,ca.card_type, pn.card_name,pn.credit_sum,ca.balance,ca.user_name,ca.status,col1 from ncme_study_card_history ca join imp_tmp_tab t\r\non substr(ca.card_no,0,10)>=t.col2\r\nand substr(ca.card_no,0,10)<=t.col3\r\nleft join user_register re on re.login_name=ca.user_name\r\nleft join ncme_pay_card pn on pn.card_type=ca.card_type', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('580', '2018-04-12', '烦请调取下课题：201700921175 北京市精神卫生知识全员培训 去年培训的总人次和申请人数，查下最高每天的培训人次，', 'select to_char(o.credit_time,\'yyyy-MM-dd\') ,count(1)  from ncme_study_course_over o where o.course_id=\'201700921175\'\r\ngroup by to_char(o.credit_time,\'yyyy-MM-dd\') \r\n\r\n\r\nselect count(1)  from ncme_study_course_over o where o.course_id=\'201700921175\'\r\ngroup by to_char(o.credit_time,\'yyyy-MM-dd\')', '1', '', '4', '90', '2', '91', '8', '56');
INSERT INTO `daily_report_dba` VALUES ('581', '2018-04-12', '执行脚本', '--查问题数据\r\nSELECT  COUNT(*)\r\n  FROM NPROJECT_ZHIXING_BOOK B\r\n WHERE EXISTS (SELECT 1\r\n          FROM NPROJECT_ZHIXING Z\r\n         WHERE 1 = 1\r\n           AND Z.ID = B.ZHIXING_ID\r\n           AND Z.PROJECT_ID <> B.PROJECT_ID\r\n           );\r\n\r\n--修改问题数据\r\nMERGE INTO nproject_zhixing_book b\r\nUSING nproject_zhixing z\r\nON (Z.ID = B.ZHIXING_ID)\r\nWHEN MATCHED THEN\r\n  UPDATE\r\n     SET b.project_id = z.project_id\r\n   WHERE b.project_id <> z.project_id;\r\n --查询学员数不一致项目  \r\nselect count(*) \r\n  from nproject_project pp,\r\n       (select p.id, count(*) cc\r\n          from nproject_project p\r\n          join nproject_zhixing_book b\r\n            on b.project_id = p.id\r\n         group by p.id) pa\r\n where pp.id = pa.id \r\n   and pp.student_count <> pa.cc\r\n--修改学员数不一致项目\r\nmerge into \r\nnproject_project pp\r\nusing (select p.id, count(*) cc\r\n          from nproject_project p\r\n          join nproject_zhixing_book b\r\n            on b.project_id = p.id\r\n         group by p.id) pa\r\n         on (pp.id = pa.id)\r\n         when matched then update set pp.student_count=pa.cc\r\n           where  pp.student_count <> pa.cc', '1', '', '66', '117', '2', '89', '44', '9');
