INSERT INTO `daily_report_dba` VALUES ('585', '2018-04-12', '烦请调取下cme网站近6个月来（2017年10月-2017年3月）网站的日均学习人次和人数和每天用户平均注册数和每天平均学习卡购买量', '--每天用户平均注册数\r\nselect count(1)/182  from user_register re where re.user_reg_time>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand    re.user_reg_time<to_date(\'2018-4-1\',\'yyyy-MM-dd\') \r\ngroup by to_char (re.user_reg_time,\'yyyy-MM-dd\');\r\n\r\n\r\n--网站的日均学习人次\r\nselect count(1) from ncme_study_credit_history\r\n re where re.applydate>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand    re.applydate<to_date(\'2018-4-1\',\'yyyy-MM-dd\'); \r\n\r\n\r\nselect (count(1)+1838870)/182 from ncme_study_credit\r\n re where re.applydate>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand    re.applydate<to_date(\'2018-4-1\',\'yyyy-MM-dd\');\r\n\r\n--网站的日均学习人数\r\nselect count(distinct user_name) from ncme_study_credit_history\r\n re where re.applydate>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand    re.applydate<to_date(\'2018-4-1\',\'yyyy-MM-dd\'); \r\n\r\n\r\nselect (count(distinct user_name)+583986)/182 from ncme_study_credit\r\n re where re.applydate>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand    re.applydate<to_date(\'2018-4-1\',\'yyyy-MM-dd\');\r\n\r\n\r\n\r\n-- 每天平均学习网上卡购买量\r\nselect  count(1)  from ncme_study_card_history h where h.operator_date\r\n>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand operator_date<to_date(\'2018-4-1\',\'yyyy-MM-dd\')\r\nand h.sale_type=2 ;\r\n\r\nselect (count(1)+67593)/182 from ncme_study_card\r\nh where h.operator_date\r\n>=to_date(\'2017-10-1\',\'yyyy-MM-dd\') \r\nand operator_date<to_date(\'2018-4-1\',\'yyyy-MM-dd\')\r\nand sale_type=2;', '1', '', '4', '90', '2', '91', '8', '56');
