INSERT INTO `data_paper_store` VALUES ('14', '数字传播-专家达', 'luhaiwei@mail.haoyisheng.com', '好医生专家达数据调取：\r\n用户名，活动月份,场次，大区，城市，医院NH编码，姓名，医院名称，科室，职称，费用，电话，身份证号，开户行，银行卡号', '王乐', '数据长期调取', '2017-08-22', 'select t.login_name as \'用户名\',t.activity_month as \'活动月份\',t.season as \'场次\',t.large_area as \'大区\',t.city as \'城市\',\r\nt.hsp_code as \'医院NH编码\',t.name as \'姓名\',t.hsp_name as \'医院\',t.dept as \'科室\',t.title as \'职称\',t.cost as \'费用\',t.mobile as \'电话\',concat(\'\\\'\',t.id_card) as \'身份证号\',t.bank_name as \'开户行\',concat(\'\\\'\',t.bank_card) as \'银行卡号\' from sys_user t\r\nwhere t.del_flag=0 and t.login_name !=\'haoyisheng\'', '好医生数调备字【2017-08-01-00-01】', '1', '2017-12-31', null, '2017-08-03', '已过期');
INSERT INTO `data_paper_store` VALUES ('15', '数字传播', 'shenyao@mail.haoyisheng.com', '商务课程cme数据调取：\r\n省,市，县，医院级别，用户名，姓名，性别，年两，单位，自填地址，科室，职称，学习状态（所有状态），付费卡号,课题名称，申请子类型，课程id,开始日期，通过日期，发证日期', '沈瑶', '暂无', '2017-08-22', 'select sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n         when \'f\' then\r\n          \'女\'\r\n         when \'m\' then\r\n          \'男\'\r\n       end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n    -- re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n    sc.card_no 付费卡号,\r\n      nc.name 课程名称,\r\n sc.apply_subtype 申请子类型,\r\nsc.startdate 开始日期,\r\n sc.passdate 通过日期,\r\n --sc.applydate 申请日期,\r\n      sc.senddate 发证日期\r\n     -- sc.apply_credit  学分,\r\n     -- sc.apply_credit*3 学时,\r\n      --ct.credit_name 学分类型\r\n  --select count(*)\r\n  from ncme_study_credit sc\r\n  LEFT JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n    join ncme_credit_type ct on ct.credit_type=sc.credit_type\r\n where    sc.course_id IN(\'201700921009\',\'201700921010\')\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\n and sc.startdate<to_date(\'2017-8-1\',\'yyyy-mm-dd\')\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nunion\r\nselect sc.course_id,\r\n       user_getdistrict(re.user_x_id, 1) 省,\r\n       user_getdistrict(re.user_x_id, 2) 市,\r\n       user_getdistrict(re.user_x_id, 3) 县,\r\n        user_getuserinfo(re.login_name,4) 医院级别,\r\n       re.login_name 用户名,\r\n       re.user_real_name 姓名,\r\n       case re.user_gender\r\n         when \'f\' then\r\n          \'女\'\r\n         when \'m\' then\r\n          \'男\'\r\n       end as 性别,\r\n       to_char(sysdate,\'yyyy\')-to_char(re.user_birthday,\'yyyy\') 年龄,\r\n     --re.user_mobile 电话,\r\n       re.user_hsp_name 单位,\r\n     re.user_address 自填地址,\r\n       User_getUserInfo(re.login_name, 1) 科室,\r\n     User_getUserInfo(re.login_name, 2) 职称,\r\n       case NCME_getStudyStatus(sc.user_name,\r\n                            sc.course_id,\r\n                            sysdate)\r\n         when 0 then\r\n          \'未学习\'\r\n         when 1 then\r\n          \'学习中\'\r\n         when 2 then\r\n          \'未申请\'\r\n         when 3 then\r\n          \'未领证(已申请)\'\r\n         when 4 then\r\n          \'已领证\'\r\n       end as 学习状态,\r\n       null 付费卡号,\r\n       nc.name 课程名称,\r\n       null 申请子类型,\r\n       sc.startdate 开始日期,\r\n       sc.passdate 通过日期,\r\n       --null 申请日期,\r\n       null 发证日期\r\n  from ncme_study_course sc\r\n JOIN user_register re\r\n    ON sc.user_name = re.login_name\r\n  left join ncme_course nc\r\n    on nc.course_id = sc.course_id\r\n where sc.course_id IN(\'201700921009\',\'201700921010\')\r\n and sc.startdate>=to_date(\'2017-2-1\',\'yyyy-mm-dd\')\r\n and sc.startdate<to_date(\'2017-8-1\',\'yyyy-mm-dd\')', '好医生数调备字【2017-08-01-00-02】', '1', '2017-12-31', '2017-8-30 调取数据  zhanglinlin@mail.haoyisheng.com', '2017-08-04', '已过期');
