INSERT INTO `data_paper_store` VALUES ('18', 'cme', 'shenyao@mail.haoyisheng.com', '省份，地市，区县，单位，用户名，姓名，性别，科室，职称，联系电话，课题编号，医院级别，年龄，自填地址，学习状态（所有状态），课题名称，付费卡号，申请子类型，开始日期，通过日期，发证日期', '沈瑶', '暂无', '2017-08-22', '2017-7-31之后之调取edu_soho数据\r\ncme数据只调取红叶列出的药企合作项目学习数据', '好医生数调备字【2017-04-27-00-01】', '1', '2017-12-31', null, '2017-04-27', '已过期');
INSERT INTO `data_paper_store` VALUES ('19', '教育培训-住院医', 'jiangkuan@mail.haoyisheng.com', '数据汇报：\r\n单位，用户名，姓名，证件号码，专业，答题时间，答题数 ，成绩', '姜宽', '有求必应', '2017-10-16', 'select t4.USER_ACCOUNT,\r\n       t4.real_name as 考生姓名,\r\n       t4.certificate_no as 考生身份证号,\r\n       t4.org_name as 所属机构,\r\n       --t4.base_name as 考试场次,\r\n       --t4.mobilnumber as 考生电话,\r\n       case\r\n         when t.state = 1 then\r\n          \'正在考试\'\r\n         when t.state = 2 then\r\n          \'完成考试\'\r\n         else\r\n          \'未考试\'\r\n       end as 考试状态,\r\n       t.answer_start_time as 答题开始时间,\r\n       t.answer_end_time as 答题结束时间,\r\n       count(t5.id) as 答题数,\r\n       sum(nvl(t5.judge_score, 0)) as 得分\r\n  from em_number_student t\r\n  left join em_test_number t2\r\n    on t.test_number_id = t2.id\r\n  left join em_test t3\r\n    on t2.test_id = t3.id\r\n  join em_user_portal t4\r\n    on t4.id = t.student_id\r\n  left join em_test_score t5\r\n    on t5.studnet_id = t.student_id\r\n   and t5.exam_course_id = t.exam_course_id\r\n where t.exam_id = 9302\r\n group by t4.USER_ACCOUNT,\r\n          t4.org_name,\r\n          t4.mobilnumber,\r\n          t4.real_name,\r\n          t4.certificate_no,\r\n          t.state,\r\n          t.answer_start_time,\r\n          t.answer_end_time,\r\n          t4.base_name\r\nselect t4.USER_ACCOUNT,\r\n       t4.real_name as 考生姓名,\r\n       t4.certificate_no as 考生身份证号,\r\n       t4.org_name as 所属机构,\r\n       t4.base_name as 考试场次,\r\n       t4.mobilnumber as 考生电话,\r\n       case\r\n         when t.state = 1 then\r\n          \'正在考试\'\r\n         when t.state = 2 then\r\n          \'完成考试\'\r\n         else\r\n          \'未考试\'\r\n       end as 考试状态,\r\n       t.answer_start_time as 答题开始时间,\r\n       t.answer_end_time as 答题结束时间,\r\n       count(t5.id) as 答题数,\r\n       sum(nvl(t5.judge_score, 0)) as 得分\r\n  from em_number_student t\r\n  left join em_test_number t2\r\n    on t.test_number_id = t2.id\r\n  left join em_test t3\r\n    on t2.test_id = t3.id\r\n  join em_user_portal t4\r\n    on t4.id = t.student_id\r\n  left join em_test_score t5\r\n    on t5.studnet_id = t.student_id\r\n   and t5.exam_course_id = t.exam_course_id\r\n where t.exam_id = 9302\r\n group by t4.USER_ACCOUNT,\r\n          t4.org_name,\r\n          t4.mobilnumber,\r\n          t4.real_name,\r\n          t4.certificate_no,\r\n          t.state,\r\n          t.answer_start_time,\r\n          t.answer_end_time,\r\n          t4.base_name', '好医生数调备字【2017-10-16-00-01】', '1', '2017-12-31', '待定', '2017-10-16', '已过期');
INSERT INTO `data_paper_store` VALUES ('21', '基层', 'jiangkuan@mail.haoyisheng.com', '单位、批次、姓名、学员ID、课件名称、学习时间', '姜宽', '根据客户时间指定', '2017-11-06', 'select * from raid d where d.org_id=0013--培训批次\r\n\r\n\r\nselect org.org_name,\r\n       d.name,\r\n       n.name,\r\n       s.student_id,\r\n       c.name,\r\n       max(scl.study_time)\r\n  from student_courseware_log scl \r\n left join students s\r\n    on s.student_id = scl.student_id\r\n left join raid d\r\n    on d.id = s.raid_id\r\n  left join person n\r\n    on n.id = s.student_id\r\n  left join raid d\r\n    on d.id = s.raid_id\r\n  join courseware c\r\n    on c.courseware_id = scl.courseware_id\r\n  left join organization org\r\n    on org.org_id = n.org_id\r\n where d.org_id = \'0013\'\r\n   and d.id = 10941 and scl.study_time <to_date(\'2017-11-06\',\'YYYY-MM-DD\')\r\n   group by scl.courseware_id,org.org_name,\r\n       d.name,\r\n       n.name,\r\n       s.student_id,\r\n       c.name \r\n   order by s.student_id', '好医生数调备字【2017-11-06-24-11】', '1', '2017-12-31', '按照客户需求提取数据', '2017-11-06', '已过期');
